parts:
{
  "name": "UBX-NAV-SLAS",
  "class_id": "0x01",
  "message_id": "0x42",
  "description": "This message outputs the status of the QZSS L1S SLAS sub system",
  "message_type": "periodic_polled",
  "payload": {
    "length": {
      "variable": {
        "base": 20,
        "formula": "20 + cnt * 8",
        "min": 20
      }
    },
    "fields": [
      {
        "name": "iTOW",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. See the description of iTOW for details.",
        "unit": "ms"
      },
      {
        "name": "version",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "reserved0",
        "byte_offset": 5,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "gmsLon",
        "byte_offset": 8,
        "data_type": "I4",
        "description": "Longitude of the used ground monitoring station",
        "unit": "deg",
        "scale": {
          "raw": "1e-3",
          "multiplier": 0.001
        }
      },
      {
        "name": "gmsLat",
        "byte_offset": 12,
        "data_type": "I4",
        "description": "Latitude of the used ground monitoring station",
        "unit": "deg",
        "scale": {
          "raw": "1e-3",
          "multiplier": 0.001
        }
      },
      {
        "name": "gmsCode",
        "byte_offset": 16,
        "data_type": "U1",
        "description": "Code of the used ground monitoring station according to the QZSS SLAS Interface Specification, available from qzss.go.jp/en/"
      },
      {
        "name": "qzssSvId",
        "byte_offset": 17,
        "data_type": "U1",
        "description": "Satellite identifier of the QZS/GEO whose correction data is used (see Satellite Numbering)"
      },
      {
        "name": "serviceFlags",
        "byte_offset": 18,
        "data_type": "X1",
        "description": "Flags regarding SLAS service",
        "bitfield": {
          "bits": [
            {
              "name": "gmsAvailable",
              "bit_start": 0,
              "bit_end": 0,
              "description": "1 = Ground monitoring station available"
            },
            {
              "name": "qzssSvAvailable",
              "bit_start": 1,
              "bit_end": 1,
              "description": "1 = Correction providing QZSS SV available"
            },
            {
              "name": "testMode",
              "bit_start": 2,
              "bit_end": 2,
              "description": "1 = Currently used QZSS SV in test mode"
            }
          ]
        }
      },
      {
        "name": "cnt",
        "byte_offset": 19,
        "data_type": "U1",
        "description": "Number of pseudorange corrections following"
      }
    ],
    "repeated_groups": [
      {
        "name": "pseudorange_corrections",
        "repetition_type": "count_field",
        "count_field": "cnt",
        "group_size_bytes": 8,
        "base_offset": 20,
        "fields": [
          {
            "name": "gnssId",
            "byte_offset": 0,
            "data_type": "U1",
            "description": "GNSS identifier (see Satellite Numbering)"
          },
          {
            "name": "svId",
            "byte_offset": 1,
            "data_type": "U1",
            "description": "Satellite identifier (see Satellite Numbering)"
          },
          {
            "name": "reserved1",
            "byte_offset": 2,
            "data_type": "U1",
            "description": "Reserved",
            "reserved": true
          },
          {
            "name": "reserved2",
            "byte_offset": 3,
            "data_type": {
              "array_of": "U1",
              "count": 3
            },
            "description": "Reserved",
            "reserved": true
          },
          {
            "name": "prc",
            "byte_offset": 6,
            "data_type": "I2",
            "description": "Pseudorange correction",
            "unit": "cm"
          }
        ]
      }
    ]
  }
}
