{
  "name": "UBX-NAV-STATUS",
  "class_id": "0x01",
  "message_id": "0x03",
  "description": "Receiver navigation status",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 16
    },
    "fields": [
      {
        "name": "iTOW",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. For details, see section iTOW timestamps in the integration manual.",
        "unit": "ms"
      },
      {
        "name": "gpsFix",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "GPSfix Type, this value does not qualify a fix as valid and within the limits. See note on flag gpsFixOk below. Possible values: 0x00 = no fix, 0x01 = dead reckoning only, 0x02 = 2D-fix, 0x03 = 3D-fix, 0x04 = GPS + dead reckoning combined, 0x05 = Time only fix, 0x06..0xff = reserved."
      },
      {
        "name": "flags",
        "byte_offset": 5,
        "data_type": "X1",
        "description": "Navigation Status Flags",
        "bitfield": {
          "bits": [
            {
              "name": "gpsFixOk",
              "bit_start": 0,
              "bit_end": 0,
              "description": "1 = position and velocity valid and within DOP and ACC Masks."
            },
            {
              "name": "diffSoln",
              "bit_start": 1,
              "bit_end": 1,
              "description": "1 = differential corrections were applied"
            },
            {
              "name": "wknSet",
              "bit_start": 2,
              "bit_end": 2,
              "description": "1 = Week Number valid (for details, see section Time validity in the Integration manual)"
            },
            {
              "name": "towSet",
              "bit_start": 3,
              "bit_end": 3,
              "description": "1 = Time of Week valid (for details, see section Time validity in the integration manual)"
            }
          ]
        }
      },
      {
        "name": "fixStat",
        "byte_offset": 6,
        "data_type": "X1",
        "description": "Fix Status Information",
        "bitfield": {
          "bits": [
            {
              "name": "diffCorr",
              "bit_start": 0,
              "bit_end": 0,
              "description": "1 = differential corrections available"
            },
            {
              "name": "carrSolnValid",
              "bit_start": 1,
              "bit_end": 1,
              "description": "1 = valid carrSoln"
            },
            {
              "name": "mapMatching",
              "bit_start": 6,
              "bit_end": 7,
              "description": "map matching status: 00: none, 01: valid but not used, i.e. map matching data was received, but was too old, 10: valid and used, map matching data was applied, 11: valid and used, map matching data was applied. In case of sensor unavailability map matching data enables dead reckoning. This requires map matched latitude/longitude or heading data."
            }
          ]
        }
      },
      {
        "name": "flags2",
        "byte_offset": 7,
        "data_type": "X1",
        "description": "further information about navigation output",
        "bitfield": {
          "bits": [
            {
              "name": "psmState",
              "bit_start": 0,
              "bit_end": 1,
              "description": "power save mode state (not supported for protocol versions less than 13.01). Possible values: 0 = ACQUISITION [or when psm disabled], 1 = TRACKING, 2 = POWER OPTIMIZED TRACKING, 3 = INACTIVE."
            },
            {
              "name": "spoofDetState",
              "bit_start": 3,
              "bit_end": 4,
              "description": "Spoofing detection state (not supported for protocol versions less than 18.00). Possible values: 0: Unknown or deactivated, 1: No spoofing indicated, 2: Spoofing indicated, 3: Multiple spoofing indications. Note that the spoofing state value only reflects the detector state for the current navigation epoch. As spoofing can be detected most easily at the transition from real signal to spoofing signal, this is also where the detector is triggered the most. I.e. a value of 1 - No spoofing indicated does not mean that the receiver is not spoofed, it simply states that the detector was not triggered in this epoch."
            },
            {
              "name": "carrSoln",
              "bit_start": 6,
              "bit_end": 7,
              "description": "Carrier phase range solution status: 0 = no carrier phase range solution, 1 = carrier phase range solution with floating ambiguities, 2 = carrier phase range solution with fixed ambiguities."
            }
          ]
        }
      },
      {
        "name": "ttff",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "Time to first fix (millisecond time tag)",
        "unit": "ms"
      },
      {
        "name": "msss",
        "byte_offset": 12,
        "data_type": "U4",
        "description": "Milliseconds since startup/reset",
        "unit": "ms"
      }
    ]
  }
}
