parts:
{
  "name": "UBX-NAV-PL",
  "class_id": "0x01",
  "message_id": "0x62",
  "description": "Protection level information",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 52
    },
    "fields": [
      {
        "name": "msgVersion",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "name": "tmirCoeff",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Target misleading information risk (TMIR) [%MI/epoch], coefficient integer number of base 10 scientific notation (see e.g. plPos field)"
      },
      {
        "name": "tmirExp",
        "byte_offset": 2,
        "data_type": "I1",
        "description": "Target misleading information risk (TMIR) [%MI/epoch], exponent integer number of base 10 scientific notation (see e.g. plPos field)"
      },
      {
        "name": "plPosValid",
        "byte_offset": 3,
        "data_type": "X1",
        "description": "Position protection level validity",
        "bitfield": {
          "bits": [
            {
              "name": "valid",
              "bit_start": 0,
              "bit_end": 0,
              "description": "0: Invalid (Protection level should not be used), 1: Protection level is valid"
            }
          ]
        }
      },
      {
        "name": "plPosFrame",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Position protection level frame: 0: Invalid (not possible to calculate frame conversion), 1: North-East-Down, 2: Longitudinal-Lateral-Vertical, 3: HorizSemiMajorAxis-HorizSemiMinorAxis-Vertical"
      },
      {
        "name": "plVelValid",
        "byte_offset": 5,
        "data_type": "X1",
        "description": "Velocity protection level validity",
        "bitfield": {
          "bits": [
            {
              "name": "valid",
              "bit_start": 0,
              "bit_end": 0,
              "description": "0: Invalid (Protection level should not be used), 1: Protection level is valid"
            }
          ]
        }
      },
      {
        "name": "plVelFrame",
        "byte_offset": 6,
        "data_type": "U1",
        "description": "Velocity protection level frame: 0: Invalid (not possible to calculate frame conversion), 1: North-East-Down, 2: Longitudinal-Lateral-Vertical, 3: HorizSemiMajorAxis-HorizSemiMinorAxis-Vertical"
      },
      {
        "name": "plTimeValid",
        "byte_offset": 7,
        "data_type": "X1",
        "description": "Time protection level validity",
        "bitfield": {
          "bits": [
            {
              "name": "valid",
              "bit_start": 0,
              "bit_end": 0,
              "description": "0: Invalid (Protection level should not be used), 1: Protection level is valid"
            }
          ]
        }
      },
      {
        "name": "plPosInvalidityReason",
        "byte_offset": 8,
        "data_type": "U1",
        "description": "Position protection level invalidity reason: 0: Not available, 1-29: Solution not trustworthy, 30-100: PL not verified for this receiver configuration"
      },
      {
        "name": "plVelInvalidityReason",
        "byte_offset": 9,
        "data_type": "U1",
        "description": "Velocity protection level invalidity reason: 0: Not available, 1-29: Solution not trustworthy, 30-100: PL not verified for this receiver configuration"
      },
      {
        "name": "plTimeInvalidityReason",
        "byte_offset": 10,
        "data_type": "U1",
        "description": "Time protection level invalidity reason: 0: Not available, 1-29: Solution not trustworthy, 30-100: PL not verified for this receiver configuration"
      },
      {
        "name": "reserved0",
        "byte_offset": 11,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "iTow",
        "byte_offset": 12,
        "data_type": "U4",
        "description": "GPS time of week",
        "unit": "ms"
      },
      {
        "name": "plPos1",
        "byte_offset": 16,
        "data_type": "U4",
        "description": "First axis of position protection level value, given in coordinate frame of plPosFrame (see plPosFrame field for value order), w.r.t. the given target misleading information risk (TMIR) of [tmirCoeff * 10^(tmirExp)]",
        "unit": "mm"
      },
      {
        "name": "plPos2",
        "byte_offset": 20,
        "data_type": "U4",
        "description": "Second axis of position protection level value, given in coordinate frame of plPosFrame (see plPosFrame field for value order), w.r.t. the given target misleading information risk (TMIR) of [tmirCoeff * 10^(tmirExp)]",
        "unit": "mm"
      },
      {
        "name": "plPos3",
        "byte_offset": 24,
        "data_type": "U4",
        "description": "Third axis of position protection level value, given in coordinate frame of plPosFrame (see plPosFrame field for value order), w.r.t. the given target misleading information risk (TMIR) of [tmirCoeff * 10^(tmirExp)]",
        "unit": "mm"
      },
      {
        "name": "plVel1",
        "byte_offset": 28,
        "data_type": "U4",
        "description": "First axis of velocity protection level value, given in coordinate frame of plVelFrame (see plVelFrame field for value order), w.r.t. the given target misleading information risk (TMIR) of [tmirCoeff * 10^(tmirExp)]",
        "unit": "mm/s"
      },
      {
        "name": "plVel2",
        "byte_offset": 32,
        "data_type": "U4",
        "description": "Second axis of velocity protection level value, given in coordinate frame of plVelFrame (see plVelFrame field for value order), w.r.t. the given target misleading information risk (TMIR) of [tmirCoeff * 10^(tmirExp)]",
        "unit": "mm/s"
      },
      {
        "name": "plVel3",
        "byte_offset": 36,
        "data_type": "U4",
        "description": "Third axis of velocity protection level value, given in coordinate frame of plVelFrame (see plVelFrame field for value order), w.r.t. the given target misleading information risk (TMIR) of [tmirCoeff * 10^(tmirExp)]",
        "unit": "mm/s"
      },
      {
        "name": "plPosHorizOrient",
        "byte_offset": 40,
        "data_type": "U2",
        "description": "Orientation of HorizSemiMajorAxis (see plPosFrame) of horizontal ellipse position protection level (clockwise degrees from true North), if plPosFrame==3; zero otherwise.",
        "unit": "deg",
        "scale": {
          "raw": "1e-2",
          "multiplier": 0.01
        }
      },
      {
        "name": "plVelHorizOrient",
        "byte_offset": 42,
        "data_type": "U2",
        "description": "Orientation of HorizSemiMajorAxis (see plVelFrame) of horizontal ellipse velocity protection level (clockwise degrees from true North), if plVelFrame==3; zero otherwise.",
        "unit": "deg",
        "scale": {
          "raw": "1e-2",
          "multiplier": 0.01
        }
      },
      {
        "name": "plTime",
        "byte_offset": 44,
        "data_type": "U4",
        "description": "Time protection level value, w.r.t. the given target misleading information risk (TMIR) of [tmirCoeff * 10^(tmirExp)]",
        "unit": "ns"
      },
      {
        "name": "reserved1",
        "byte_offset": 48,
        "data_type": {
          "array_of": "U1",
          "count": 4
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
