parts:
{
  "name": "UBX-NAV-SAT",
  "class_id": "0x01",
  "message_id": "0x35",
  "description": "Satellite information",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 8,
        "formula": "8 + numSvs * 12",
        "min": 8
      }
    },
    "fields": [
      {
        "name": "iTow",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details.",
        "unit": "ms"
      },
      {
        "name": "version",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "name": "numSvs",
        "byte_offset": 5,
        "data_type": "U1",
        "description": "Number of satellites"
      },
      {
        "name": "reserved0",
        "byte_offset": 6,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "repeated_group_sv",
        "repetition_type": "count_field",
        "count_field": "numSvs",
        "group_size_bytes": 12,
        "base_offset": 8,
        "fields": [
          {
            "name": "gnssId",
            "byte_offset": 0,
            "data_type": "U1",
            "description": "GNSS identifier (see Satellite Numbering) for assignment"
          },
          {
            "name": "svId",
            "byte_offset": 1,
            "data_type": "U1",
            "description": "Satellite identifier (see Satellite Numbering) for assignment"
          },
          {
            "name": "cno",
            "byte_offset": 2,
            "data_type": "U1",
            "description": "Carrier to noise ratio (signal strength)",
            "unit": "dBHz"
          },
          {
            "name": "elev",
            "byte_offset": 3,
            "data_type": "I1",
            "description": "Elevation (range: +/-90), unknown if out of range",
            "unit": "deg"
          },
          {
            "name": "azim",
            "byte_offset": 4,
            "data_type": "I2",
            "description": "Azimuth (range 0-360), unknown if elevation is out of range",
            "unit": "deg"
          },
          {
            "name": "prRes",
            "byte_offset": 6,
            "data_type": "I2",
            "description": "Pseudorange residual",
            "scale": {
              "raw": "0.1",
              "multiplier": 0.1
            },
            "unit": "m"
          },
          {
            "name": "flags",
            "byte_offset": 8,
            "data_type": "X4",
            "description": "Bitmask",
            "bitfield": {
              "bits": [
                {
                  "name": "qualityInd",
                  "bit_start": 0,
                  "bit_end": 2,
                  "description": "Signal quality indicator: 0 = no signal, 1 = searching signal, 2 = signal acquired, 3 = signal detected but unusable, 4 = code locked and time synchronized"
                },
                {
                  "name": "svUsed",
                  "bit_start": 3,
                  "bit_end": 3,
                  "description": "1 = Signal in the subset specified in Signal Identifiers is currently being used for navigation"
                },
                {
                  "name": "health",
                  "bit_start": 4,
                  "bit_end": 5,
                  "description": "Signal health flag: 0 = unknown, 1 = healthy, 2 = unhealthy"
                },
                {
                  "name": "diffCorr",
                  "bit_start": 6,
                  "bit_end": 6,
                  "description": "1 = differential correction data is available for this SV"
                },
                {
                  "name": "smoothed",
                  "bit_start": 7,
                  "bit_end": 7,
                  "description": "1 = carrier smoothed pseudorange used"
                },
                {
                  "name": "orbitSource",
                  "bit_start": 8,
                  "bit_end": 10,
                  "description": "Orbit source: 0 = no orbit information is available for this SV, 1 = ephemeris is used, 2 = almanac is used, 3 = AssistNow Offline orbit is used, 4 = AssistNow Autonomous orbit is used, 5, 6, 7 = other orbit information is used"
                },
                {
                  "name": "ephAvail",
                  "bit_start": 11,
                  "bit_end": 11,
                  "description": "1 = ephemeris is available for this SV"
                },
                {
                  "name": "almAvail",
                  "bit_start": 12,
                  "bit_end": 12,
                  "description": "1 = almanac is available for this SV"
                },
                {
                  "name": "anoAvail",
                  "bit_start": 13,
                  "bit_end": 13,
                  "description": "1 = AssistNow Offline data is available for this SV"
                },
                {
                  "name": "aopAvail",
                  "bit_start": 14,
                  "bit_end": 14,
                  "description": "1 = AssistNow Autonomous data is available for this SV"
                },
                {
                  "name": "reserved1",
                  "bit_start": 15,
                  "bit_end": 15,
                  "description": "Reserved",
                  "reserved": true
                },
                {
                  "name": "sbasCorrUsed",
                  "bit_start": 16,
                  "bit_end": 16,
                  "description": "1 = SBAS corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "rtcmCorrUsed",
                  "bit_start": 17,
                  "bit_end": 17,
                  "description": "1 = RTCM corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "slasCorrUsed",
                  "bit_start": 18,
                  "bit_end": 18,
                  "description": "1 = QZSS SLAS corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "spartnCorrUsed",
                  "bit_start": 19,
                  "bit_end": 19,
                  "description": "1 = SPARTN corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "prCorrUsed",
                  "bit_start": 20,
                  "bit_end": 20,
                  "description": "1 = Pseudorange corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "crCorrUsed",
                  "bit_start": 21,
                  "bit_end": 21,
                  "description": "1 = Carrier range corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "doCorrUsed",
                  "bit_start": 22,
                  "bit_end": 22,
                  "description": "1 = Range rate (Doppler) corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "clasCorrUsed",
                  "bit_start": 23,
                  "bit_end": 23,
                  "description": "1 = CLAS corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "lppCorrUsed",
                  "bit_start": 24,
                  "bit_end": 24,
                  "description": "1 = LPP corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "hasCorrUsed",
                  "bit_start": 25,
                  "bit_end": 25,
                  "description": "1 = HAS corrections have been used for a signal in the subset specified in Signal Identifiers"
                },
                {
                  "name": "reserved2",
                  "bit_start": 26,
                  "bit_end": 31,
                  "description": "Reserved",
                  "reserved": true
                }
              ]
            }
          }
        ]
      }
    ]
  }
}
