parts:
{
  "name": "UBX-MON-COMMS",
  "class_id": "0x0a",
  "message_id": "0x36",
  "description": "Communication port information",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 8,
        "formula": "8 + nPorts*40",
        "min": 8
      }
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "nPorts",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Number of ports included"
      },
      {
        "name": "txErrors",
        "byte_offset": 2,
        "data_type": "X1",
        "description": "TX error bitmask",
        "bitfield": {
          "bits": [
            {
              "name": "mem",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Memory Allocation error"
            },
            {
              "name": "alloc",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Allocation error (TX buffer full)"
            }
          ]
        }
      },
      {
        "name": "reserved0",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "protIds",
        "byte_offset": 4,
        "data_type": {
          "array_of": "U1",
          "count": 4
        },
        "description": "The identifiers of the protocols reported in the msgs array. 0: UBX, 1: NMEA, 2: RTCM2, 5: RTCM3, 6: SPARTN, 0xFF: No protocol reported."
      }
    ],
    "repeated_groups": [
      {
        "name": "ports",
        "repetition_type": "count_field",
        "count_field": "nPorts",
        "group_size_bytes": 40,
        "base_offset": 8,
        "fields": [
          {
            "name": "portId",
            "byte_offset": 0,
            "data_type": "U2",
            "description": "Unique identifier for the port. See section Communications ports in Integration manual for details."
          },
          {
            "name": "txPending",
            "byte_offset": 2,
            "data_type": "U2",
            "description": "Number of bytes pending in transmitter buffer",
            "unit": "bytes"
          },
          {
            "name": "txBytes",
            "byte_offset": 4,
            "data_type": "U4",
            "description": "Number of bytes ever sent",
            "unit": "bytes"
          },
          {
            "name": "txUsage",
            "byte_offset": 8,
            "data_type": "U1",
            "description": "Maximum usage transmitter buffer during the last sysmon period",
            "unit": "%"
          },
          {
            "name": "txPeakUsage",
            "byte_offset": 9,
            "data_type": "U1",
            "description": "Maximum usage transmitter buffer",
            "unit": "%"
          },
          {
            "name": "rxPending",
            "byte_offset": 10,
            "data_type": "U2",
            "description": "Number of bytes in receiver buffer",
            "unit": "bytes"
          },
          {
            "name": "rxBytes",
            "byte_offset": 12,
            "data_type": "U4",
            "description": "Number of bytes ever received",
            "unit": "bytes"
          },
          {
            "name": "rxUsage",
            "byte_offset": 16,
            "data_type": "U1",
            "description": "Maximum usage receiver buffer during the last sysmon period",
            "unit": "%"
          },
          {
            "name": "rxPeakUsage",
            "byte_offset": 17,
            "data_type": "U1",
            "description": "Maximum usage receiver buffer",
            "unit": "%"
          },
          {
            "name": "overrunErrs",
            "byte_offset": 18,
            "data_type": "U2",
            "description": "Number of 100 ms timeslots with overrun errors"
          },
          {
            "name": "msgs",
            "byte_offset": 20,
            "data_type": {
              "array_of": "U2",
              "count": 4
            },
            "description": "Number of successfully parsed messages for each protocol. The reported protocols are identified through the protIds field.",
            "unit": "msg"
          },
          {
            "name": "reserved1",
            "byte_offset": 28,
            "data_type": {
              "array_of": "U1",
              "count": 8
            },
            "description": "Reserved",
            "reserved": true
          },
          {
            "name": "skipped",
            "byte_offset": 36,
            "data_type": "U4",
            "description": "Number of skipped bytes",
            "unit": "bytes"
          }
        ]
      }
    ]
  }
}
