parts:
{
  "name": "UBX-MON-IO",
  "class_id": "0x0a",
  "message_id": "0x02",
  "description": "I/O system status",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 22,
        "formula": "22 + nPins * 6",
        "min": 22
      }
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "nPins",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "The number of I/O pins included"
      },
      {
        "name": "flags",
        "byte_offset": 2,
        "data_type": "X1",
        "description": "Flags",
        "bitfield": {
          "bits": [
            {
              "name": "rtcCalib",
              "bit_start": 0,
              "bit_end": 0,
              "description": "RTC is calibrated"
            },
            {
              "name": "safeBoot",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Safeboot mode (0 = inactive, 1 = active)"
            },
            {
              "name": "xtalAbsent",
              "bit_start": 2,
              "bit_end": 2,
              "description": "RTC xtal has been determined to be absent"
            }
          ]
        }
      },
      {
        "name": "hwVersion",
        "byte_offset": 3,
        "data_type": {
          "array_of": "CH",
          "count": 10
        },
        "description": "Zero-terminated hardware version string (same as that returned in the UBX-MON-VER message)"
      },
      {
        "name": "reserved0",
        "byte_offset": 13,
        "data_type": {
          "array_of": "U1",
          "count": 9
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "pin_info",
        "repetition_type": "count_field",
        "count_field": "nPins",
        "group_size_bytes": 6,
        "base_offset": 22,
        "fields": [
          {
            "name": "pinId",
            "byte_offset": 0,
            "data_type": "U2",
            "description": "Identifier for the pin, including both external and internal pins."
          },
          {
            "name": "pinMask",
            "byte_offset": 2,
            "data_type": "X2",
            "description": "Pin mask",
            "bitfield": {
              "bits": [
                {
                  "name": "periphPIO",
                  "bit_start": 0,
                  "bit_end": 0,
                  "description": "Pin is set to peripheral or PIO? 0=Peripheral 1=PIO"
                },
                {
                  "name": "pinBank",
                  "bit_start": 1,
                  "bit_end": 3,
                  "description": "Bank the pin belongs to, where 0=A 1=B 2=C 3=D 4=E 5=F 6=G 7=H"
                },
                {
                  "name": "direction",
                  "bit_start": 4,
                  "bit_end": 4,
                  "description": "Pin direction? 0=Input 1=Output"
                },
                {
                  "name": "value",
                  "bit_start": 5,
                  "bit_end": 5,
                  "description": "Pin value? 0=Low 1=High"
                },
                {
                  "name": "vpManager",
                  "bit_start": 6,
                  "bit_end": 6,
                  "description": "Used by virtual pin manager? 0=No 1=Yes"
                },
                {
                  "name": "pioIrq",
                  "bit_start": 7,
                  "bit_end": 7,
                  "description": "Interrupt enabled? 0=No 1=Yes"
                },
                {
                  "name": "pioPullHigh",
                  "bit_start": 8,
                  "bit_end": 8,
                  "description": "Using pull high resistor? 0=No 1=Yes"
                },
                {
                  "name": "pioPullLow",
                  "bit_start": 9,
                  "bit_end": 9,
                  "description": "Using pull low resistor 0=No 1=Yes"
                }
              ]
            }
          },
          {
            "name": "VP",
            "byte_offset": 4,
            "data_type": "U1",
            "description": "Virtual pin mapping"
          },
          {
            "name": "reserved1",
            "byte_offset": 5,
            "data_type": "U1",
            "description": "Reserved",
            "reserved": true
          }
        ]
      }
    ]
  }
}
