parts:
{
  "name": "UBX-RXM-PMP",
  "class_id": "0x02",
  "message_id": "0x72",
  "description": "Point to Multipoint (LBAND) input message",
  "message_type": "input",
  "payload": {
    "length": {
      "variable": {
        "base": 24,
        "formula": "24 + numBytesUserData",
        "min": 24
      }
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "name": "reserved0",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "numBytesUserData",
        "byte_offset": 2,
        "data_type": "U2",
        "description": "Number of bytes the userData block has in this frame (0...504)"
      },
      {
        "name": "timeTag",
        "byte_offset": 4,
        "data_type": "U4",
        "description": "Time since startup when frame started - if max value of type is reached the counter will be reset",
        "unit": "ms"
      },
      {
        "name": "uniqueWord",
        "byte_offset": 8,
        "data_type": {
          "array_of": "U4",
          "count": 2
        },
        "description": "Received unique words"
      },
      {
        "name": "serviceIdentifier",
        "byte_offset": 16,
        "data_type": "U2",
        "description": "Received service identifier"
      },
      {
        "name": "spare",
        "byte_offset": 18,
        "data_type": "U1",
        "description": "Received spare data"
      },
      {
        "name": "uniqueWordBitErrors",
        "byte_offset": 19,
        "data_type": "U1",
        "description": "Number of bit errors in both unique words"
      },
      {
        "name": "fecBits",
        "byte_offset": 20,
        "data_type": "U2",
        "description": "Number of bits corrected by FEC (forward error correction)"
      },
      {
        "name": "ebno",
        "byte_offset": 22,
        "data_type": "U1",
        "description": "Energy per bit to noise power spectral density ratio",
        "scale": {
          "raw": "2^-3",
          "multiplier": 0.125
        },
        "unit": "dB"
      },
      {
        "name": "reserved1",
        "byte_offset": 23,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "userData",
        "byte_offset": 24,
        "data_type": {
          "array_of": "U1",
          "count": "numBytesUserData"
        },
        "description": "Received user data, which is variable (=numBytesUserData)"
      }
    ]
  }
}
