parts:
{
  "name": "UBX-MON-RF",
  "class_id": "0x0a",
  "message_id": "0x38",
  "description": "Information for each RF block. There are as many RF blocks reported as bands supported by this receiver.",
  "message_type": "periodic_polled",
  "payload": {
    "length": {
      "variable": {
        "base": 4,
        "formula": "4 + nBlocks * 20",
        "min": 4
      }
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "nBlocks",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "The number of RF blocks included"
      },
      {
        "name": "reserved0",
        "byte_offset": 2,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "rf_blocks",
        "repetition_type": "count_field",
        "count_field": "nBlocks",
        "group_size_bytes": 20,
        "base_offset": 4,
        "fields": [
          {
            "name": "blockId",
            "byte_offset": 0,
            "data_type": "U1",
            "description": "RF block ID (0 = L1 band, 1 = L2 or L5 band depending on product configuration)"
          },
          {
            "name": "antStatus",
            "byte_offset": 1,
            "data_type": "U1",
            "description": "Status of the antenna supervisor state machine (0x00=INIT, 0x01=DONTKNOW, 0x02=OK, 0x03=SHORT, 0x04=OPEN)"
          },
          {
            "name": "antPower",
            "byte_offset": 2,
            "data_type": "U1",
            "description": "Current power status of antenna (0x00=OFF, 0x01=ON, 0x02=DONTKNOW)"
          },
          {
            "name": "cwSuppression",
            "byte_offset": 3,
            "data_type": "U1",
            "description": "CW interference suppression level, scaled (0=no CW jamming, 255 = strong CW jamming)"
          },
          {
            "name": "postStatus",
            "byte_offset": 4,
            "data_type": "U4",
            "description": "POST status word"
          },
          {
            "name": "reserved1",
            "byte_offset": 8,
            "data_type": {
              "array_of": "U1",
              "count": 4
            },
            "description": "Reserved",
            "reserved": true
          },
          {
            "name": "noisePerMS",
            "byte_offset": 12,
            "data_type": "U2",
            "description": "Noise level as measured by the GPS core"
          },
          {
            "name": "agcCnt",
            "byte_offset": 14,
            "data_type": "U2",
            "description": "AGC Monitor, as percentage of maximum gain, range 0 to 8191 (100%)"
          },
          {
            "name": "ofsI",
            "byte_offset": 16,
            "data_type": "I1",
            "description": "Imbalance of I-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
          },
          {
            "name": "magI",
            "byte_offset": 17,
            "data_type": "U1",
            "description": "Magnitude of I-part of complex signal, scaled (0 = no signal, 255 = max.magnitude)"
          },
          {
            "name": "ofsQ",
            "byte_offset": 18,
            "data_type": "I1",
            "description": "Imbalance of Q-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
          },
          {
            "name": "magQ",
            "byte_offset": 19,
            "data_type": "U1",
            "description": "Magnitude of Q-part of complex signal, scaled (0 = no signal, 255 = max.magnitude)"
          }
        ]
      }
    ]
  }
}
