parts:
{
  "name": "UBX-NAV-ORB",
  "class_id": "0x01",
  "message_id": "0x34",
  "description": "GNSS orbit database info",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 8,
        "formula": "8 + numSv * 6",
        "min": 8
      }
    },
    "fields": [
      {
        "name": "iTOW",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details.",
        "unit": "ms"
      },
      {
        "name": "version",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "name": "numSv",
        "byte_offset": 5,
        "data_type": "U1",
        "description": "Number of SVs in the database"
      },
      {
        "name": "reserved0",
        "byte_offset": 6,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "satellites",
        "repetition_type": "count_field",
        "count_field": "numSv",
        "group_size_bytes": 6,
        "base_offset": 8,
        "fields": [
          {
            "name": "gnssId",
            "byte_offset": 0,
            "data_type": "U1",
            "description": "GNSS ID"
          },
          {
            "name": "svId",
            "byte_offset": 1,
            "data_type": "U1",
            "description": "Satellite ID"
          },
          {
            "name": "svFlag",
            "byte_offset": 2,
            "data_type": "X1",
            "description": "Information Flags",
            "bitfield": {
              "bits": [
                {
                  "name": "health",
                  "bit_start": 0,
                  "bit_end": 1,
                  "description": "SV health: 0 = unknown, 1 = healthy, 2 = not healty"
                },
                {
                  "name": "visibility",
                  "bit_start": 2,
                  "bit_end": 3,
                  "description": "SV health: 0 = unknown, 1 = below horizon, 2 = above horizon, 3 = above elevation mask"
                }
              ]
            }
          },
          {
            "name": "eph",
            "byte_offset": 3,
            "data_type": "X1",
            "description": "Ephemeris data",
            "bitfield": {
              "bits": [
                {
                  "name": "ephUsability",
                  "bit_start": 0,
                  "bit_end": 4,
                  "description": "How long the receiver will be able to use the stored ephemeris data from now on: 31 = The usability period is unknown, 30 = The usability period is more than 450 minutes, 30 > n > 0 = The usability period is between (n-1)*15 and n*15 minutes, 0 = Ephemeris can no longer be used"
                },
                {
                  "name": "ephSource",
                  "bit_start": 5,
                  "bit_end": 7,
                  "description": "0 = not available, 1 = GNSS transmission, 2 = external aiding, 3-7 = other"
                }
              ]
            }
          },
          {
            "name": "alm",
            "byte_offset": 4,
            "data_type": "X1",
            "description": "Almanac data",
            "bitfield": {
              "bits": [
                {
                  "name": "almUsability",
                  "bit_start": 0,
                  "bit_end": 4,
                  "description": "How long the receiver will be able to use the stored almanac data from now on: 31 = The usability period is unknown, 30 = The usability period is more than 30 days, 30 > n > 0 = The usability period is between n-1 and n days, 0 = Almanac can no longer be used"
                },
                {
                  "name": "almSource",
                  "bit_start": 5,
                  "bit_end": 7,
                  "description": "0 = not available"
                }
              ]
            }
          },
          {
            "name": "otherOrb",
            "byte_offset": 5,
            "data_type": "X1",
            "description": "Other orbit data available",
            "bitfield": {
              "bits": [
                {
                  "name": "anoAopUsability",
                  "bit_start": 0,
                  "bit_end": 4,
                  "description": "How long the receiver will be able to use the orbit data from now on: 31 = The usability period is unknown, 30 = The usability period is more than 30 days, 30 > n > 0 = The usability period is between n-1 and n days, 0 = Data can no longer be used"
                },
                {
                  "name": "type",
                  "bit_start": 5,
                  "bit_end": 7,
                  "description": "Type of orbit data: 0 = No orbit data available, 1 = AssistNow Offline data, 2 = AssistNow Autonomous data, 3-7 = Other orbit data"
                }
              ]
            }
          }
        ]
      }
    ]
  }
}
