parts:
{
  "name": "UBX-MON-HW2",
  "class_id": "0x0a",
  "message_id": "0x0b",
  "description": "Status of different aspects of the hardware, such as antenna, PIO/peripheral pins, noise level, automatic gain control (AGC)",
  "message_type": "periodic_polled",
  "payload": {
    "length": {
      "fixed": 60
    },
    "fields": [
      {
        "name": "pinSel",
        "byte_offset": 0,
        "data_type": "X4",
        "description": "Mask of pins set as peripheral/PIO"
      },
      {
        "name": "pinBank",
        "byte_offset": 4,
        "data_type": "X4",
        "description": "Mask of pins set as bank A/B"
      },
      {
        "name": "pinDir",
        "byte_offset": 8,
        "data_type": "X4",
        "description": "Mask of pins set as input/output"
      },
      {
        "name": "pinVal",
        "byte_offset": 12,
        "data_type": "X4",
        "description": "Mask of pins value low/high"
      },
      {
        "name": "noisePerMS",
        "byte_offset": 16,
        "data_type": "U2",
        "description": "Noise level as measured by the GPS core"
      },
      {
        "name": "agcCnt",
        "byte_offset": 18,
        "data_type": "U2",
        "description": "AGC Monitor, as percentage of maximum gain, range 0 to 8191 (100%)"
      },
      {
        "name": "aStatus",
        "byte_offset": 20,
        "data_type": "U1",
        "description": "Status of the antenna supervisor state machine (0=INIT, 1=DONTKNOW, 2=OK, 3=SHORT, 4=OPEN)"
      },
      {
        "name": "aPower",
        "byte_offset": 21,
        "data_type": "U1",
        "description": "Current power status of antenna (0=OFF, 1=ON, 2=DONTKNOW)"
      },
      {
        "name": "flags",
        "byte_offset": 22,
        "data_type": "X1",
        "description": "Flags",
        "bitfield": {
          "bits": [
            {
              "name": "rtcCalib",
              "bit_start": 0,
              "bit_end": 0,
              "description": "RTC is calibrated"
            },
            {
              "name": "safeBoot",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Safeboot mode (0 = inactive, 1 = active)"
            },
            {
              "name": "jammingState",
              "bit_start": 2,
              "bit_end": 3,
              "description": "Output from jamming/interference monitor (0 = unknown or feature disabled or flag unavailable, 1 = ok - no significant jamming, 2 = warning - interference visible but fix OK, 3 = critical - interference visible and no fix). This flag is deprecated in protocol versions that support UBX-SEC-SIG (version 0x02) and always reported as 0; instead jammingState in UBX-SEC-SIG should be monitored."
            },
            {
              "name": "xtalAbsent",
              "bit_start": 4,
              "bit_end": 4,
              "description": "RTC xtal has been determined to be absent (not supported for protocol versions less than 18.00)"
            }
          ]
        }
      },
      {
        "name": "reserved0",
        "byte_offset": 23,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "usedMask",
        "byte_offset": 24,
        "data_type": "X4",
        "description": "Mask of pins that are used by the virtual pin manager"
      },
      {
        "name": "VP",
        "byte_offset": 28,
        "data_type": {
          "array_of": "U1",
          "count": 17
        },
        "description": "Array of pin mappings for each of the 17 physical pins"
      },
      {
        "name": "cwSuppression",
        "byte_offset": 45,
        "data_type": "U1",
        "description": "CW interference suppression level, scaled (0 = no CW jamming, 255 = strong CW jamming)"
      },
      {
        "name": "reserved1",
        "byte_offset": 46,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "pinIrq",
        "byte_offset": 48,
        "data_type": "X4",
        "description": "Mask of pins value using the PIO Irq"
      },
      {
        "name": "pullH",
        "byte_offset": 52,
        "data_type": "X4",
        "description": "Mask of pins value using the PIO pull high resistor"
      },
      {
        "name": "pullL",
        "byte_offset": 56,
        "data_type": "X4",
        "description": "Mask of pins value using the PIO pull low resistor"
      }
    ]
  }
}
