parts:
{
  "name": "UBX-MON-SYS",
  "class_id": "0x0a",
  "message_id": "0x39",
  "description": "Current system performance information",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 24
    },
    "fields": [
      {
        "name": "msgVer",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message Version (0x01)"
      },
      {
        "name": "bootType",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Boot type of master chip. 0-Unknown, 1-Cold Start, 2-Watchdog, 3-Hardware reset, 4-Hardware backup, 5-Software backup, 6-Software reset, 7-VIO fail, 8-VDD_X fail, 9-VDD_RF fail, 10-V_CORE_HIGH fail."
      },
      {
        "name": "cpuLoad",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Highest actual load of realtime tasks of all CPUs",
        "unit": "%"
      },
      {
        "name": "cpuLoadMax",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Maximal CPU load value seen since last restart",
        "unit": "%"
      },
      {
        "name": "memUsage",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Highest actual dynamic memory usage of all CPUs",
        "unit": "%"
      },
      {
        "name": "memUsageMax",
        "byte_offset": 5,
        "data_type": "U1",
        "description": "Maximal dynamic memory usage seen since last restart",
        "unit": "%"
      },
      {
        "name": "ioUsage",
        "byte_offset": 6,
        "data_type": "U1",
        "description": "Highest actual IO bandwidth usage of all rx/tx interfaces",
        "unit": "%"
      },
      {
        "name": "ioUsageMax",
        "byte_offset": 7,
        "data_type": "U1",
        "description": "Maximal bandwidth usage of all rx/tx interfaces seen since last restart",
        "unit": "%"
      },
      {
        "name": "runTime",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "Time since last restart",
        "unit": "sec"
      },
      {
        "name": "noticeCount",
        "byte_offset": 12,
        "data_type": "U2",
        "description": "Number of notices occurred since last restart"
      },
      {
        "name": "warnCount",
        "byte_offset": 14,
        "data_type": "U2",
        "description": "Number of warnings occurred since last restart"
      },
      {
        "name": "errorCount",
        "byte_offset": 16,
        "data_type": "U2",
        "description": "Number of errors occurred since last restart"
      },
      {
        "name": "tempValue",
        "byte_offset": 18,
        "data_type": "I1",
        "description": "Temperature value",
        "unit": "C"
      },
      {
        "name": "reserved0",
        "byte_offset": 19,
        "data_type": {
          "array_of": "U1",
          "count": 5
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
