parts:
{
  "name": "UBX-RXM-PMREQ-CMD",
  "class_id": "0x02",
  "message_id": "0x41",
  "description": "This message requests a power management related task of the receiver.",
  "message_type": "command",
  "payload": {
    "length": {
      "fixed": 16
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "reserved0",
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "duration",
        "byte_offset": 4,
        "data_type": "U4",
        "description": "Duration of the requested task. The maximum supported value is 12 days. Set to 0 to wait for a wakeup signal on a pin",
        "unit": "ms"
      },
      {
        "name": "flags",
        "byte_offset": 8,
        "data_type": "X4",
        "description": "task flags",
        "bitfield": {
          "bits": [
            {
              "name": "backup",
              "bit_start": 1,
              "bit_end": 1,
              "description": "The receiver goes into backup mode for a time period defined by duration, provided that it is not connected to USB"
            }
          ]
        }
      },
      {
        "name": "wakeupSources",
        "byte_offset": 12,
        "data_type": "X4",
        "description": "Configure pins to wake up the receiver. The receiver wakes up if there is either a falling or a rising edge on one of the configured pins.",
        "bitfield": {
          "bits": [
            {
              "name": "force",
              "bit_start": 2,
              "bit_end": 2,
              "description": "Force receiver backup while USB is connected. USB interface will be disabled."
            },
            {
              "name": "uartrx",
              "bit_start": 3,
              "bit_end": 3,
              "description": "Wake up the receiver if there is an edge on the UART RX pin"
            },
            {
              "name": "extint0",
              "bit_start": 5,
              "bit_end": 5,
              "description": "Wake up the receiver if there is an edge on the EXTINT0 pin"
            },
            {
              "name": "extint1",
              "bit_start": 6,
              "bit_end": 6,
              "description": "Wake up the receiver if there is an edge on the EXTINT1 pin"
            },
            {
              "name": "spics",
              "bit_start": 7,
              "bit_end": 7,
              "description": "Wake up the receiver if there is an edge on the SPI CS pin"
            }
          ]
        }
      }
    ]
  }
}
