parts:
{
  "name": "UBX-SEC-SIG",
  "class_id": "0x27",
  "message_id": "0x09",
  "description": "Signal security information",
  "message_type": "periodic_polled",
  "payload": {
    "length": {
      "variable": {
        "base": 4,
        "formula": "4 + jamNumCentFreqs * 4",
        "min": 4
      }
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x02 for this version)"
      },
      {
        "name": "sigSecFlags",
        "byte_offset": 1,
        "data_type": "X1",
        "description": "Signal security flags, providing high-level jamming and spoofing detector information",
        "bitfield": {
          "bits": [
            {
              "name": "jamDetEnabled",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Flag indicates whether jamming detection is enabled"
            },
            {
              "name": "jamState",
              "bit_start": 1,
              "bit_end": 2,
              "description": "Jamming state\n0: Unknown\n1: No jamming indicated\n2: Warning; jamming indicated\n0: Unknown, denotes that the currently available information is not sufficient to judge whether the receiver is jammed or not. This may occur at receiver start up (or more generally when the receiver is in a mode, where jamming detection is hindered) or when the jamming indicator is disabled. 1: No jamming indicated: the jamming indicator is enabled and does not sense any significant jamming. 2: Warning; jamming indicated: the jamming indicator is indicating jamming which has a significant impact on the signal tracking. (The list jamPerCentFreq can be checked to find out which frequency bands are jammed.)"
            },
            {
              "name": "spfDetEnabled",
              "bit_start": 3,
              "bit_end": 3,
              "description": "Flag indicates whether spoofing detection is enabled"
            },
            {
              "name": "spfState",
              "bit_start": 4,
              "bit_end": 6,
              "description": "Spoofing state\n0: Unknown\n1: No spoofing indicated\n2: Spoofing indicated\n3: Spoofing affirmed"
            }
          ]
        }
      },
      {
        "name": "reserved0",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "jamNumCentFreqs",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "The number of center frequencies we provide jamming information for (subsequent messages)"
      }
    ],
    "repeated_groups": [
      {
        "name": "jamCentFreqsInfo",
        "repetition_type": "count_field",
        "count_field": "jamNumCentFreqs",
        "group_size_bytes": 4,
        "base_offset": 4,
        "fields": [
          {
            "name": "jamStateCentFreq",
            "byte_offset": 0,
            "data_type": "X4",
            "description": "Jamming state of signals sharing a given center frequency\nNote that jamming information is only provided for center frequencies related to at least one in-use signal, for which a sufficient amount of information is currently available to judge if it is affected by jamming.",
            "bitfield": {
              "bits": [
                {
                  "name": "centFreq",
                  "bit_start": 0,
                  "bit_end": 23,
                  "description": "Center frequency in [kHz], floored to the nearest kHz multiple"
                },
                {
                  "name": "jammed",
                  "bit_start": 24,
                  "bit_end": 24,
                  "description": "Flag indicates whether signals on the given center frequency are considered jammed"
                }
              ]
            }
          }
        ]
      }
    ]
  }
}
