parts:
{
  "name": "UBX-RXM-MEASX",
  "class_id": "0x02",
  "message_id": "0x14",
  "description": "Satellite measurements for RRLP",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 44,
        "formula": "44 + numSV * 24",
        "min": 44
      }
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version, currently 0x01"
      },
      {
        "name": "reserved0",
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "gpsTOW",
        "byte_offset": 4,
        "data_type": "U4",
        "description": "GPS measurement reference time",
        "unit": "ms"
      },
      {
        "name": "gloTOW",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "GLONASS measurement reference time",
        "unit": "ms"
      },
      {
        "name": "bdsTOW",
        "byte_offset": 12,
        "data_type": "U4",
        "description": "BeiDou measurement reference time",
        "unit": "ms"
      },
      {
        "name": "reserved1",
        "byte_offset": 16,
        "data_type": {
          "array_of": "U1",
          "count": 4
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "qzssTOW",
        "byte_offset": 20,
        "data_type": "U4",
        "description": "QZSS measurement reference time",
        "unit": "ms"
      },
      {
        "name": "gpsTOWacc",
        "byte_offset": 24,
        "data_type": "U2",
        "description": "GPS measurement reference time accuracy (0xffff = > 4s)",
        "unit": "ms",
        "scale": {
          "raw": "2^-4",
          "multiplier": 0.0625
        }
      },
      {
        "name": "gloTOWacc",
        "byte_offset": 26,
        "data_type": "U2",
        "description": "GLONASS measurement reference time accuracy (0xffff = > 4s)",
        "unit": "ms",
        "scale": {
          "raw": "2^-4",
          "multiplier": 0.0625
        }
      },
      {
        "name": "bdsTOWacc",
        "byte_offset": 28,
        "data_type": "U2",
        "description": "BeiDou measurement reference time accuracy (0xffff = > 4s)",
        "unit": "ms",
        "scale": {
          "raw": "2^-4",
          "multiplier": 0.0625
        }
      },
      {
        "name": "reserved2",
        "byte_offset": 30,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "qzssTOWacc",
        "byte_offset": 32,
        "data_type": "U2",
        "description": "QZSS measurement reference time accuracy (0xffff = > 4s)",
        "unit": "ms",
        "scale": {
          "raw": "2^-4",
          "multiplier": 0.0625
        }
      },
      {
        "name": "numSV",
        "byte_offset": 34,
        "data_type": "U1",
        "description": "Number of satellites in repeated block"
      },
      {
        "name": "flags",
        "byte_offset": 35,
        "data_type": "X1",
        "description": "Flags",
        "bitfield": {
          "bits": [
            {
              "name": "towSet",
              "bit_start": 0,
              "bit_end": 1,
              "description": "TOW set (0 = no, 1 or 2 = yes)"
            }
          ]
        }
      },
      {
        "name": "reserved3",
        "byte_offset": 36,
        "data_type": {
          "array_of": "U1",
          "count": 8
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "repeated_group_numSV",
        "repetition_type": "count_field",
        "count_field": "numSV",
        "group_size_bytes": 24,
        "base_offset": 44,
        "fields": [
          {
            "name": "gnssId",
            "byte_offset": 0,
            "data_type": "U1",
            "description": "GNSS ID (see Satellite Numbering)"
          },
          {
            "name": "svId",
            "byte_offset": 1,
            "data_type": "U1",
            "description": "Satellite ID (see Satellite Numbering)"
          },
          {
            "name": "cNo",
            "byte_offset": 2,
            "data_type": "U1",
            "description": "carrier noise ratio (0..63)"
          },
          {
            "name": "mpathIndic",
            "byte_offset": 3,
            "data_type": "U1",
            "description": "multipath index (according to [1]) (0 = not measured, 1 = low, 2 = medium, 3 = high)"
          },
          {
            "name": "dopplerMS",
            "byte_offset": 4,
            "data_type": "I4",
            "description": "Doppler measurement",
            "unit": "m/s",
            "scale": {
              "raw": "0.04",
              "multiplier": 0.04
            }
          },
          {
            "name": "dopplerHz",
            "byte_offset": 8,
            "data_type": "I4",
            "description": "Doppler measurement",
            "unit": "Hz",
            "scale": {
              "raw": "0.2",
              "multiplier": 0.2
            }
          },
          {
            "name": "wholeChips",
            "byte_offset": 12,
            "data_type": "U2",
            "description": "whole value of the code phase measurement (0..1022 for GPS)"
          },
          {
            "name": "fracChips",
            "byte_offset": 14,
            "data_type": "U2",
            "description": "fractional value of the code phase measurement (0..1023)"
          },
          {
            "name": "codePhase",
            "byte_offset": 16,
            "data_type": "U4",
            "description": "Code phase",
            "unit": "ms",
            "scale": {
              "raw": "2^-21",
              "multiplier": 0.000000476837158203125
            }
          },
          {
            "name": "intCodePhase",
            "byte_offset": 20,
            "data_type": "U1",
            "description": "Integer (part of the) code phase",
            "unit": "ms"
          },
          {
            "name": "pseuRangeRMS_Err",
            "byte_offset": 21,
            "data_type": "U1",
            "description": "pseudorange RMS error index (according to [1]) (0..63)"
          },
          {
            "name": "reserved4",
            "byte_offset": 22,
            "data_type": {
              "array_of": "U1",
              "count": 2
            },
            "description": "Reserved",
            "reserved": true
          }
        ]
      }
    ]
  }
}
