parts:
{
  "name": "UBX-RXM-RAWX",
  "class_id": "0x02",
  "message_id": "0x15",
  "description": "Multi-GNSS raw measurements",
  "message_type": "periodic_polled",
  "payload": {
    "length": {
      "variable": {
        "base": 16,
        "formula": "16 + numMeas * 32",
        "min": 16
      }
    },
    "fields": [
      {
        "name": "rcvTow",
        "byte_offset": 0,
        "data_type": "R8",
        "description": "Measurement time of week in receiver local time approximately aligned to the GPS time system.",
        "unit": "s"
      },
      {
        "name": "week",
        "byte_offset": 8,
        "data_type": "U2",
        "description": "GPS week number in receiver local time.",
        "unit": "weeks"
      },
      {
        "name": "leaps",
        "byte_offset": 10,
        "data_type": "I1",
        "description": "GPS leap seconds (GPS-UTC). This field represents the receiver's best knowledge of the leap seconds offset. A flag is given in the recStat bitfield to indicate if the leap seconds are known.",
        "unit": "s"
      },
      {
        "name": "numMeas",
        "byte_offset": 11,
        "data_type": "U1",
        "description": "Number of measurements to follow"
      },
      {
        "name": "recStat",
        "byte_offset": 12,
        "data_type": "X1",
        "description": "Receiver tracking status bitfield",
        "bitfield": {
          "bits": [
            {
              "name": "leapSec",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Leap seconds have been determined"
            },
            {
              "name": "clkReset",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Clock reset applied. Typically the receiver clock is changed in increments of integer milliseconds."
            }
          ]
        }
      },
      {
        "name": "version",
        "byte_offset": 13,
        "data_type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "name": "reserved0",
        "byte_offset": 14,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "measurements",
        "repetition_type": "count_field",
        "count_field": "numMeas",
        "group_size_bytes": 32,
        "base_offset": 16,
        "fields": [
          {
            "name": "prMes",
            "byte_offset": 0,
            "data_type": "R8",
            "description": "Pseudorange measurement [m]. GLONASS inter frequency channel delays are compensated with an internal calibration table.",
            "unit": "m"
          },
          {
            "name": "cpMes",
            "byte_offset": 8,
            "data_type": "R8",
            "description": "Carrier phase measurement [cycles]. The carrier phase initial ambiguity is initialized using an approximate value to make the magnitude of the phase close to the pseudorange measurement. Clock resets are applied to both phase and code measurements in accordance with the RINEX specification.",
            "unit": "cycles"
          },
          {
            "name": "doMes",
            "byte_offset": 16,
            "data_type": "R4",
            "description": "Doppler measurement (positive sign for approaching satellites) [Hz]",
            "unit": "Hz"
          },
          {
            "name": "gnssId",
            "byte_offset": 20,
            "data_type": "U1",
            "description": "GNSS identifier (see Satellite Numbering for a list of identifiers)"
          },
          {
            "name": "svId",
            "byte_offset": 21,
            "data_type": "U1",
            "description": "Satellite identifier (see Satellite Numbering)"
          },
          {
            "name": "sigId",
            "byte_offset": 22,
            "data_type": "U1",
            "description": "New style signal identifier (see Signal Identifiers).(not supported for protocol versions less than 27.00)"
          },
          {
            "name": "freqId",
            "byte_offset": 23,
            "data_type": "U1",
            "description": "Only used for GLONASS: This is the frequency slot + 7 (range from 0 to 13)"
          },
          {
            "name": "locktime",
            "byte_offset": 24,
            "data_type": "U2",
            "description": "Carrier phase locktime counter (maximum 64500ms)",
            "unit": "ms"
          },
          {
            "name": "cno",
            "byte_offset": 26,
            "data_type": "U1",
            "description": "Carrier-to-noise density ratio (signal strength) [dB-Hz]",
            "unit": "dBHz"
          },
          {
            "name": "prStdev",
            "byte_offset": 27,
            "data_type": "X1",
            "description": "Estimated pseudorange measurement standard deviation",
            "scale": {
              "raw": "0.01*2^n",
              "multiplier": null
            },
            "unit": "m",
            "bitfield": {
              "bits": [
                {
                  "name": "prStd",
                  "bit_start": 0,
                  "bit_end": 3,
                  "data_type": "U4",
                  "description": "Estimated pseudorange standard deviation"
                }
              ]
            }
          },
          {
            "name": "cpStdev",
            "byte_offset": 28,
            "data_type": "X1",
            "description": "Estimated carrier phase measurement standard deviation (note a raw value of 0x0F indicates the value is invalid)",
            "scale": {
              "raw": "0.004",
              "multiplier": 0.004
            },
            "unit": "cycles",
            "bitfield": {
              "bits": [
                {
                  "name": "cpStd",
                  "bit_start": 0,
                  "bit_end": 3,
                  "data_type": "U4",
                  "description": "Estimated carrier phase standard deviation"
                }
              ]
            }
          },
          {
            "name": "doStdev",
            "byte_offset": 29,
            "data_type": "X1",
            "description": "Estimated Doppler measurement standard deviation.",
            "scale": {
              "raw": "0.002*2^n",
              "multiplier": null
            },
            "unit": "Hz",
            "bitfield": {
              "bits": [
                {
                  "name": "doStd",
                  "bit_start": 0,
                  "bit_end": 3,
                  "data_type": "U4",
                  "description": "Estimated Doppler standard deviation"
                }
              ]
            }
          },
          {
            "name": "trkStat",
            "byte_offset": 30,
            "data_type": "X1",
            "description": "Tracking status bitfield",
            "bitfield": {
              "bits": [
                {
                  "name": "prValid",
                  "bit_start": 0,
                  "bit_end": 0,
                  "description": "Pseudorange valid"
                },
                {
                  "name": "cpValid",
                  "bit_start": 1,
                  "bit_end": 1,
                  "description": "Carrier phase valid"
                },
                {
                  "name": "halfCyc",
                  "bit_start": 2,
                  "bit_end": 2,
                  "description": "Half cycle valid"
                },
                {
                  "name": "subHalfCyc",
                  "bit_start": 3,
                  "bit_end": 3,
                  "description": "Half cycle subtracted from phase"
                }
              ]
            }
          },
          {
            "name": "reserved1",
            "byte_offset": 31,
            "data_type": "U1",
            "description": "Reserved",
            "reserved": true
          }
        ]
      }
    ]
  }
}
