{
  "name": "UBX-TIM-TP",
  "class_id": "0x0d",
  "message_id": "0x01",
  "description": "Time pulse time data. This message contains information on the timing of the next pulse at the TIMEPULSEO output. The recommended configuration when using this message is to set both the measurement rate (CFG-RATE) and the timepulse frequency (CFG-TP) to 1 Hz.",
  "message_type": "periodic_polled",
  "payload": {
    "length": {
      "fixed": 16
    },
    "fields": [
      {
        "name": "towMS",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "Time pulse time of week according to time base",
        "unit": "ms"
      },
      {
        "name": "towSubMS",
        "byte_offset": 4,
        "data_type": "U4",
        "description": "Submillisecond part of towMS",
        "unit": "ms",
        "scale": {
          "raw": "2^-32",
          "multiplier": 2.3283064365386963e-10
        }
      },
      {
        "name": "qErr",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "Quantization error of time pulse",
        "unit": "ps"
      },
      {
        "name": "week",
        "byte_offset": 12,
        "data_type": "U2",
        "description": "Time pulse week number according to time base",
        "unit": "weeks"
      },
      {
        "name": "flags",
        "byte_offset": 14,
        "data_type": "X1",
        "description": "Flags",
        "bitfield": {
          "bits": [
            {
              "name": "timeBase",
              "bit_start": 0,
              "bit_end": 0,
              "description": "0 = Time base is GNSS; 1 = Time base is UTC"
            },
            {
              "name": "utc",
              "bit_start": 1,
              "bit_end": 1,
              "description": "0 = UTC not available; 1 = UTC available"
            },
            {
              "name": "raim",
              "bit_start": 2,
              "bit_end": 3,
              "description": "(T)RAIM information: 0 = Information not available; 1 = Not active; 2 = Active"
            },
            {
              "name": "qErrInvalid",
              "bit_start": 4,
              "bit_end": 4,
              "description": "0 = Quantization error valid; 1 = Quantization error invalid"
            },
            {
              "name": "TpNotLocked",
              "bit_start": 5,
              "bit_end": 5,
              "description": "0 = Next TP is locked to GNSS; 1 = Next TP is based on local time and not locked to GNSS - week/tow may be invalid"
            }
          ]
        }
      },
      {
        "name": "refInfo",
        "byte_offset": 15,
        "data_type": "X1",
        "description": "Time reference information",
        "bitfield": {
          "bits": [
            {
              "name": "timeRefGnss",
              "bit_start": 0,
              "bit_end": 3,
              "description": "GNSS reference information. Only valid if time base is GNSS (timeBase=0): 0 = GPS; 1 = GLONASS; 2 = BeiDou; 3 = Galileo; 4 = NavIC; 15 = Unknown"
            }
          ]
        }
      }
    ]
  }
}
