parts:
{
  "name": "UBX-TIM-VRFY",
  "class_id": "0x0d",
  "message_id": "0x06",
  "description": "Sourced time verification",
  "message_type": "output",
  "payload": {
    "length": {
      "fixed": 20
    },
    "fields": [
      {
        "name": "itow",
        "byte_offset": 0,
        "data_type": "I4",
        "description": "integer millisecond tow received by source",
        "unit": "ms"
      },
      {
        "name": "frac",
        "byte_offset": 4,
        "data_type": "I4",
        "description": "sub-millisecond part of tow",
        "unit": "ns"
      },
      {
        "name": "deltaMs",
        "byte_offset": 8,
        "data_type": "I4",
        "description": "integer milliseconds of delta time (current time minus sourced time)",
        "unit": "ms"
      },
      {
        "name": "deltaNs",
        "byte_offset": 12,
        "data_type": "I4",
        "description": "Sub-millisecond part of delta time",
        "unit": "ns"
      },
      {
        "name": "wno",
        "byte_offset": 16,
        "data_type": "U2",
        "description": "Week number",
        "unit": "week"
      },
      {
        "name": "flags",
        "byte_offset": 18,
        "data_type": "X1",
        "description": "Flags",
        "bitfield": {
          "bits": [
            {
              "name": "src",
              "bit_start": 0,
              "bit_end": 2,
              "description": "Aiding time source: 0 = no time aiding done; 2 = source was RTC; 3 = source was assistance data"
            }
          ]
        }
      },
      {
        "name": "reserved0",
        "byte_offset": 19,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
