{
  "name": "UBX-UPD-SOS",
  "class_id": "0x09",
  "message_id": "0x14",
  "description": "Message for backup and restore operations of the battery-backed memory (BBR) in flash, supporting polling, creation, clearing, and status reporting.",
  "message_type": "command",
  "payload": {
    "length": {
      "variable": {
        "base": 0,
        "max": 8
      }
    },
    "fields": [
      {
        "name": "cmd",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Command / Status type. Determines payload interpretation and length. When length is 0, this field is not present (Poll request). When present:\n0 = Create backup (Input)\n1 = Clear backup (Input)\n2 = Poll backup restore status (Input, expecting an 8-byte response payload)\n3 = System restored from backup (Output, 8-byte payload)"
      },
      {
        "name": "reserved0",
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved. Present in 4-byte and 8-byte payloads.",
        "reserved": true
      },
      {
        "name": "response",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Response status. Only present for 8-byte payloads (when cmd is 2 or 3).\nIf cmd = 2 (Poll backup restore status - Input):\n0 = Not acknowledged\n1 = Acknowledged\nIf cmd = 3 (System restored from backup - Output):\n0 = Unknown\n1 = Failed restoring from backup\n2 = Restored from backup\n3 = Not restored (no backup)"
      },
      {
        "name": "reserved1",
        "byte_offset": 5,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved. Present in 8-byte payloads.",
        "reserved": true
      }
    ]
  }
}
