parts:
{
  "ubx_message_extraction": {
    "name": "UBX-CFG-ANT",
    "class_id": "0x06",
    "message_id": "0x13",
    "message_type": "get_set",
    "payload_length": {
      "fixed": 4
    },
    "payload": [
      {
        "byte_offset": 0,
        "name": "flags",
        "data_type": "X2",
        "description": "Antenna flag mask",
        "fields": [
          {
            "bit_offset": 0,
            "bit_length": 1,
            "name": "SVCS",
            "data_type": "U1",
            "description": "Enable antenna supply voltage control signal"
          },
          {
            "bit_offset": 1,
            "bit_length": 1,
            "name": "scd",
            "data_type": "U1",
            "description": "Enable short circuit detection"
          },
          {
            "bit_offset": 2,
            "bit_length": 1,
            "name": "ocd",
            "data_type": "U1",
            "description": "Enable open circuit detection"
          },
          {
            "bit_offset": 3,
            "bit_length": 1,
            "name": "pdwnOnSCD",
            "data_type": "U1",
            "description": "Power down antenna supply if short circuit is detected. (only in combination with bit 1)"
          },
          {
            "bit_offset": 4,
            "bit_length": 1,
            "name": "recovery",
            "data_type": "U1",
            "description": "Enable automatic recovery from short state"
          },
          {
            "bit_offset": 5,
            "bit_length": 11,
            "name": "reserved1",
            "reserved": true
          }
        ]
      },
      {
        "byte_offset": 2,
        "name": "pins",
        "data_type": "X2",
        "description": "Antenna pin configuration",
        "fields": [
          {
            "bit_offset": 0,
            "bit_length": 5,
            "name": "pinSwitch",
            "data_type": "U5",
            "description": "PIO-pin used for switching antenna supply"
          },
          {
            "bit_offset": 5,
            "bit_length": 5,
            "name": "pinSCD",
            "data_type": "U5",
            "description": "PIO-pin used for detecting a short in the antenna supply"
          },
          {
            "bit_offset": 10,
            "bit_length": 5,
            "name": "pinOCD",
            "data_type": "U5",
            "description": "PIO-pin used for detecting open/not connected antenna"
          },
          {
            "bit_offset": 15,
            "bit_length": 1,
            "name": "reconfig",
            "data_type": "U1",
            "description": "if set to one, and this command is sent to the receiver, the receiver will reconfigure the pins as specified."
          }
        ]
      }
    ]
  }
}
