parts:
{
  "name": "UBX-CFG-GEOFENCE",
  "class_id": "0x06",
  "message_id": "0x69",
  "description": "Geofencing configuration",
  "message_type": "get_set",
  "payload_length": {
    "variable": {
      "base": 8,
      "formula": "8 + numFences * 12"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "version",
      "description": "Message version (0x00 for this version)",
      "fixed_value": 0
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "numFences",
      "description": "Number of geofences contained in this message. Note that the receiver can only store a limited number of geofences (currently 4)."
    },
    {
      "byte_offset": 2,
      "data_type": "U1",
      "name": "confLvl",
      "description": "Required confidence level for state evaluation. This value times the position's standard deviation (sigma) defines the confidence band. 0 = no confidence required; 1 = 68%; 2 = 95%; 3 = 99.7%; 4 = 99.99%"
    },
    {
      "byte_offset": 3,
      "data_type": "U1",
      "name": "reserved0",
      "reserved": true
    },
    {
      "byte_offset": 4,
      "data_type": "U1",
      "name": "pioEnabled",
      "description": "1 = Enable PIO combined fence state output, 0 = disable"
    },
    {
      "byte_offset": 5,
      "data_type": "U1",
      "name": "pinPolarity",
      "description": "PIO pin polarity. 0 = Low means inside, 1 = Low means outside. Unknown state is always high."
    },
    {
      "byte_offset": 6,
      "data_type": "U1",
      "name": "pin",
      "description": "PIO pin number"
    },
    {
      "byte_offset": 7,
      "data_type": "U1",
      "name": "reserved1",
      "reserved": true
    },
    {
      "byte_offset": 8,
      "data_type": {
        "array_of": {
          "byte_offset": 0,
          "fields": [
            {
              "byte_offset": 0,
              "data_type": "I4",
              "name": "lat",
              "scale": {
                "multiplier": 0.0000001
              },
              "unit": "deg",
              "description": "Latitude of the geofence circle center"
            },
            {
              "byte_offset": 4,
              "data_type": "I4",
              "name": "lon",
              "scale": {
                "multiplier": 0.0000001
              },
              "unit": "deg",
              "description": "Longitude of the geofence circle center"
            },
            {
              "byte_offset": 8,
              "data_type": "U4",
              "name": "radius",
              "scale": {
                "multiplier": 0.01
              },
              "unit": "m",
              "description": "Radius of the geofence circle"
            }
          ],
          "size": 12
        },
        "count_field": "numFences"
      },
      "name": "geofences",
      "description": "Repeated block for each geofence"
    }
  ]
}
