{
  "ubx_message": {
    "name": "UBX-CFG-NMEA",
    "class": "0x06",
    "id": "0x17",
    "length": 20,
    "type": "Get/set",
    "comment": "This message is deprecated in protocol versions greater than 23.01. Use UBX-CFG-VALSET, UBX-CFG-VALGET, UBX-CFG-VALDEL instead. Get/set the NMEA protocol configuration. See section NMEA Protocol Configuration for a detailed description of the configuration effects on NMEA output. See the Legacy UBX Message Fields Reference for the corresponding configuration item.",
    "fields": [
      {
        "byte_offset": 0,
        "name": "filter",
        "type": "X1",
        "description": "filter flags",
        "bits": [
          {
            "bit_offset": 0,
            "length": 1,
            "name": "posFilt",
            "type": "U1",
            "description": "Enable position output for failed or invalid fixes"
          },
          {
            "bit_offset": 1,
            "length": 1,
            "name": "mskPosFilt",
            "type": "U1",
            "description": "Enable position output for invalid fixes"
          },
          {
            "bit_offset": 2,
            "length": 1,
            "name": "timeFilt",
            "type": "U1",
            "description": "Enable time output for invalid times"
          },
          {
            "bit_offset": 3,
            "length": 1,
            "name": "dateFilt",
            "type": "U1",
            "description": "Enable date output for invalid dates"
          },
          {
            "bit_offset": 4,
            "length": 1,
            "name": "gpsOnlyFilter",
            "type": "U1",
            "description": "Restrict output to GPS satellites only"
          },
          {
            "bit_offset": 5,
            "length": 1,
            "name": "trackFilt",
            "type": "U1",
            "description": "Enable COG output even if COG is frozen"
          }
        ]
      },
      {
        "byte_offset": 1,
        "name": "nmeaVersion",
        "type": "U1",
        "description": "NMEA protocol version",
        "values": [
          {
            "value": "0x4b",
            "description": "NMEA version 4.11 (not available in all products)"
          },
          {
            "value": "0x41",
            "description": "NMEA version 4.10 (not available in all products)"
          },
          {
            "value": "0x40",
            "description": "NMEA version 4.0 (not available in all products)"
          },
          {
            "value": "0x23",
            "description": "NMEA version 2.3"
          },
          {
            "value": "0x21",
            "description": "NMEA version 2.1"
          }
        ]
      },
      {
        "byte_offset": 2,
        "name": "numSV",
        "type": "U1",
        "description": "Maximum number of SVs to report per TalkerId.",
        "values": [
          {
            "value": "0",
            "description": "unlimited"
          },
          {
            "value": "8",
            "description": "8 SVs"
          },
          {
            "value": "12",
            "description": "12 SVs"
          },
          {
            "value": "16",
            "description": "16 SVs"
          }
        ]
      },
      {
        "byte_offset": 3,
        "name": "flags",
        "type": "X1",
        "description": "flags",
        "bits": [
          {
            "bit_offset": 0,
            "length": 1,
            "name": "compat",
            "type": "U1",
            "description": "enable compatibility mode. This might be needed for certain applications when customer's NMEA parser expects a fixed number of digits in position coordinates."
          },
          {
            "bit_offset": 1,
            "length": 1,
            "name": "consider",
            "type": "U1",
            "description": "enable considering mode."
          },
          {
            "bit_offset": 2,
            "length": 1,
            "name": "limit82",
            "type": "U1",
            "description": "enable strict limit to 82 characters maximum."
          },
          {
            "bit_offset": 3,
            "length": 1,
            "name": "highPrec",
            "type": "U1",
            "description": "enable high precision mode. This flag cannot be set in conjunction with either compatibility mode or Limit82 mode (not supported for protocol versions less than 20.01)."
          }
        ]
      },
      {
        "byte_offset": 4,
        "name": "gnssToFilter",
        "type": "X4",
        "description": "Filters out satellites based on their GNSS. If a bitfield is enabled, the corresponding satellites will be not output.",
        "bits": [
          {
            "bit_offset": 0,
            "length": 1,
            "name": "gps",
            "type": "U1",
            "description": "Disable reporting of GPS satellites"
          },
          {
            "bit_offset": 1,
            "length": 1,
            "name": "sbas",
            "type": "U1",
            "description": "Disable reporting of SBAS satellites"
          },
          {
            "bit_offset": 2,
            "length": 1,
            "name": "galileo",
            "type": "U1",
            "description": "Disable reporting of Galileo satellites"
          },
          {
            "bit_offset": 4,
            "length": 1,
            "name": "qzss",
            "type": "U1",
            "description": "Disable reporting of QZSS satellites"
          },
          {
            "bit_offset": 5,
            "length": 1,
            "name": "glonass",
            "type": "U1",
            "description": "Disable reporting of GLONASS satellites"
          },
          {
            "bit_offset": 6,
            "length": 1,
            "name": "beidou",
            "type": "U1",
            "description": "Disable reporting of BeiDou satellites"
          }
        ]
      },
      {
        "byte_offset": 8,
        "name": "svNumbering",
        "type": "U1",
        "description": "Configures the display of satellites that do not have an NMEA-defined value. Note: this does not apply to satellites with an unknown ID.",
        "values": [
          {
            "value": "0",
            "description": "Strict - Satellites are not output"
          },
          {
            "value": "1",
            "description": "Extended - Use proprietary numbering (see Satellite Numbering)"
          }
        ]
      },
      {
        "byte_offset": 9,
        "name": "mainTalkerId",
        "type": "U1",
        "description": "Main Talker ID override. By default the main Talker ID (i.e. the Talker ID used for all messages other than GSV) is determined by the GNSS assignment of the receiver's channels (see UBX-CFG-GNSS).",
        "values": [
          {
            "value": "0",
            "description": "Main Talker ID is not overridden"
          },
          {
            "value": "1",
            "description": "Set main Talker ID to 'GP'"
          },
          {
            "value": "2",
            "description": "Set main Talker ID to 'GL'"
          },
          {
            "value": "3",
            "description": "Set main Talker ID to 'GN'"
          },
          {
            "value": "4",
            "description": "Set main Talker ID to 'GA' (not supported for protocol versions less than 15.00)"
          },
          {
            "value": "5",
            "description": "Set main Talker ID to 'GB' (not supported for protocol versions less than 15.00)"
          },
          {
            "value": "6",
            "description": "Set main Talker ID to 'GQ' (available in NMEA 4.11 and later)"
          }
        ]
      },
      {
        "byte_offset": 10,
        "name": "gsvTalkerId",
        "type": "U1",
        "description": "GSV Talker ID override. By default the Talker ID for GSV messages is GNSS-specific (as defined by NMEA).",
        "values": [
          {
            "value": "0",
            "description": "Use GNSS-specific Talker ID (as defined by NMEA)"
          },
          {
            "value": "1",
            "description": "Use the main Talker ID"
          }
        ]
      },
      {
        "byte_offset": 11,
        "name": "version",
        "type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "byte_offset": 12,
        "name": "bdsTalkerId_char1",
        "type": "CH",
        "size": 1,
        "description": "First character of BeiDou Talker ID. Sets the two characters that should be used for the BeiDou Talker ID. If these are set to zero, then the default BeiDou Talker ID will be used."
      },
      {
        "byte_offset": 13,
        "name": "bdsTalkerId_char2",
        "type": "CH",
        "size": 1,
        "description": "Second character of BeiDou Talker ID. Sets the two characters that should be used for the BeiDou Talker ID. If these are set to zero, then the default BeiDou Talker ID will be used."
      },
      {
        "byte_offset": 14,
        "name": "reserved0",
        "type": "U1",
        "array_length": 6,
        "description": "Reserved"
      }
    ]
  }
}
