{
  "ubx_message_extraction": {
    "message_name": "UBX-CFG-ODO",
    "class_id": "0x06",
    "message_id": "0x1e",
    "message_type": "get_set",
    "payload_length": {
      "fixed": 20
    },
    "comment": "This message is deprecated in protocol versions greater than 23.01. Use UBX-CFG-VALSET, UBX-CFG-VALGET, UBX-CFG-VALDEL instead. See the Legacy UBX Message Fields Reference for the corresponding configuration item. This feature is not supported for the FTS product variant.",
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "name": "reserved0",
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "flags",
        "byte_offset": 4,
        "data_type": "X1",
        "description": "Odometer/Low-speed COG filter flags",
        "bitfield": [
          {
            "name": "useODO",
            "data_type": "U1",
            "position": 0,
            "description": "Odometer-enabled flag"
          },
          {
            "name": "useCOG",
            "data_type": "U1",
            "position": 1,
            "description": "Low-speed COG filter enabled flag"
          },
          {
            "name": "outLPVel",
            "data_type": "U1",
            "position": 2,
            "description": "Output low-pass filtered velocity flag"
          },
          {
            "name": "outLPCog",
            "data_type": "U1",
            "position": 3,
            "description": "Output low-pass filtered heading (COG) flag"
          }
        ]
      },
      {
        "name": "odoCfg",
        "byte_offset": 5,
        "data_type": "X1",
        "description": "Odometer filter settings",
        "bitfield": [
          {
            "name": "profile",
            "data_type": "U3",
            "position": 0,
            "description": "Profile type (0=running, 1=cycling, 2=swimming, 3=car, 4=custom)"
          }
        ]
      },
      {
        "name": "reserved1",
        "byte_offset": 6,
        "data_type": {
          "array_of": "U1",
          "count": 6
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "cogMaxSpeed",
        "byte_offset": 12,
        "data_type": "U1",
        "scale": {
          "raw": "1e-1",
          "multiplier": 0.1
        },
        "unit": "m/s",
        "description": "Speed below which course-over-ground (COG) is computed with the low-speed COG filter"
      },
      {
        "name": "cogMaxPosAcc",
        "byte_offset": 13,
        "data_type": "U1",
        "unit": "m",
        "description": "Maximum acceptable position accuracy for computing COG with the low-speed COG filter"
      },
      {
        "name": "reserved2",
        "byte_offset": 14,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "velLpGain",
        "byte_offset": 16,
        "data_type": "U1",
        "description": "Velocity low-pass filter level, range 0..255"
      },
      {
        "name": "cogLpGain",
        "byte_offset": 17,
        "data_type": "U1",
        "description": "COG low-pass filter level (at speed < 8 m/s), range 0..255"
      },
      {
        "name": "reserved3",
        "byte_offset": 18,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
