parts:
{
  "name": "UBX-CFG-PRT",
  "class_id": "0x06",
  "message_id": "0x00",
  "message_type": "get_set",
  "payload_length": {
    "fixed": 20
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "portID",
      "description": "Port identifier number (see the integration manual for valid UART port IDs)"
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "reserved0",
      "reserved": true
    },
    {
      "byte_offset": 2,
      "data_type": "X2",
      "name": "txReady",
      "description": "Enable TX ready feature for this port",
      "bit_fields": [
        {
          "bit_offset": 0,
          "num_bits": 1,
          "name": "en",
          "description": "Enable TX ready feature"
        },
        {
          "bit_offset": 1,
          "num_bits": 1,
          "name": "pol",
          "description": "Polarity (0: High-active, 1: Low-active)"
        },
        {
          "bit_offset": 2,
          "num_bits": 5,
          "name": "pin",
          "description": "PIO to be used (must not be in use by another function)"
        },
        {
          "bit_offset": 7,
          "num_bits": 9,
          "name": "thres",
          "description": "Threshold (multiplied by 8 bytes, TX ready PIN goes active after >= thres*8 bytes pending for the port)"
        }
      ]
    },
    {
      "byte_offset": 4,
      "data_type": "X4",
      "name": "mode",
      "description": "A bit mask describing the UART mode",
      "bit_fields": [
        {
          "bit_offset": 0,
          "num_bits": 6,
          "name": "reserved_0",
          "reserved": true
        },
        {
          "bit_offset": 6,
          "num_bits": 2,
          "name": "charLen",
          "description": "Character length (00: 5bit, 01: 6bit, 10: 7bit, 11: 8bit)"
        },
        {
          "bit_offset": 8,
          "num_bits": 1,
          "name": "reserved_1",
          "reserved": true
        },
        {
          "bit_offset": 9,
          "num_bits": 3,
          "name": "parity",
          "description": "Parity (000: Even, 001: Odd, 10X: No parity, X1X: Reserved)"
        },
        {
          "bit_offset": 12,
          "num_bits": 2,
          "name": "nStopBits",
          "description": "Number of Stop bits (00: 1 Stop, 01: 1.5 Stop, 10: 2 Stop, 11: 0.5 Stop)"
        },
        {
          "bit_offset": 14,
          "num_bits": 18,
          "name": "reserved_2",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 8,
      "data_type": "U4",
      "name": "baudRate",
      "unit": "Bits/s",
      "description": "Baud rate in bits/second"
    },
    {
      "byte_offset": 12,
      "data_type": "X2",
      "name": "inProtoMask",
      "description": "A mask describing which input protocols are active.",
      "bit_fields": [
        {
          "bit_offset": 0,
          "num_bits": 1,
          "name": "inUbx",
          "description": "UBX protocol"
        },
        {
          "bit_offset": 1,
          "num_bits": 1,
          "name": "inNmea",
          "description": "NMEA protocol"
        },
        {
          "bit_offset": 2,
          "num_bits": 1,
          "name": "inRtcm",
          "description": "RTCM2 protocol"
        },
        {
          "bit_offset": 3,
          "num_bits": 2,
          "name": "reserved_0",
          "reserved": true
        },
        {
          "bit_offset": 5,
          "num_bits": 1,
          "name": "inRtcm3",
          "description": "RTCM3 protocol (not supported for protocol versions less than 20.00)"
        },
        {
          "bit_offset": 6,
          "num_bits": 10,
          "name": "reserved_1",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 14,
      "data_type": "X2",
      "name": "outProtoMask",
      "description": "A mask describing which output protocols are active.",
      "bit_fields": [
        {
          "bit_offset": 0,
          "num_bits": 1,
          "name": "outUbx",
          "description": "UBX protocol"
        },
        {
          "bit_offset": 1,
          "num_bits": 1,
          "name": "outNmea",
          "description": "NMEA protocol"
        },
        {
          "bit_offset": 2,
          "num_bits": 3,
          "name": "reserved_0",
          "reserved": true
        },
        {
          "bit_offset": 5,
          "num_bits": 1,
          "name": "outRtcm3",
          "description": "RTCM3 protocol (not supported for protocol versions less than 20.00)"
        },
        {
          "bit_offset": 6,
          "num_bits": 10,
          "name": "reserved_1",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 16,
      "data_type": "X2",
      "name": "flags",
      "description": "Flags bit mask",
      "bit_fields": [
        {
          "bit_offset": 0,
          "num_bits": 1,
          "name": "reserved_0",
          "reserved": true
        },
        {
          "bit_offset": 1,
          "num_bits": 1,
          "name": "extendedTxTimeout",
          "description": "Extended TX timeout: if set, port times out if allocated TX memory >=4 kB and no activity for 1.5s. If not set, port times out if no activity for 1.5s regardless of allocated TX memory (not supported for protocol versions less than 13.01)."
        },
        {
          "bit_offset": 2,
          "num_bits": 14,
          "name": "reserved_1",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 18,
      "data_type": {
        "array_of": "U1",
        "count": 2
      },
      "name": "reserved1",
      "reserved": true
    }
  ]
}
