{
  "ubx_message": {
    "name": "UBX-CFG-RINV",
    "class_id": "0x06",
    "message_id": "0x34",
    "message_type": "get_set",
    "description": "Contents of remote inventory",
    "comment": "This message is deprecated in protocol versions greater than 23.01. Use UBX-CFG-VALSET, UBX-CFG-VALGET, UBX-CFG-VALDEL instead. If N is greater than 30, the excess bytes are discarded. See the Legacy UBX Message Fields Reference for the corresponding configuration item.",
    "payload_length": {
      "variable": {
        "base": 1,
        "formula": "1 + n"
      }
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "name": "flags",
        "data_type": "X1",
        "description": "Flags",
        "bit_fields": [
          {
            "name": "dump",
            "data_type": "U:1",
            "description": "Dump data at startup. Does not work if flag binary is set."
          },
          {
            "name": "binary",
            "data_type": "U:1",
            "description": "Data is binary."
          }
        ]
      },
      {
        "byte_offset": 1,
        "name": "data",
        "data_type": {
          "array_of": "U1",
          "count": "n"
        },
        "description": "Data to store/stored in remote inventory."
      }
    ]
  }
}
