parts:
{
  "ubx_message": {
    "name": "UBX-CFG-TMODE2",
    "description": "Time mode settings 2",
    "message_type": "get_set",
    "class_id": "0x06",
    "message_id": "0x3d",
    "payload_length": {
      "fixed": 28
    },
    "fields": [
      {
        "byte_offset": 0,
        "name": "timeMode",
        "data_type": "U1",
        "description": "Time Transfer Mode: 0 Disabled 1 Survey In 2 Fixed Mode (true position information required) 3-255 Reserved"
      },
      {
        "byte_offset": 1,
        "name": "reserved0",
        "data_type": "U1",
        "reserved": true,
        "description": "Reserved"
      },
      {
        "byte_offset": 2,
        "name": "flags",
        "data_type": "X2",
        "description": "Time mode flags",
        "sub_fields": [
          {
            "name": "lla",
            "data_type": "U1",
            "bit_offset": 0,
            "bit_length": 1,
            "description": "Position is given in LAT/LON/ALT (default is ECEF)"
          },
          {
            "name": "altInv",
            "data_type": "U1",
            "bit_offset": 1,
            "bit_length": 1,
            "description": "Altitude is not valid, in case lla was set"
          }
        ]
      },
      {
        "byte_offset": 4,
        "name": "ecefXOrLat",
        "data_type": "I4",
        "scale": {
          "raw": "cm_or_deg*1e-7"
        },
        "description": "WGS84 ECEF X coordinate or latitude, depending on flags above"
      },
      {
        "byte_offset": 8,
        "name": "ecefYOrLon",
        "data_type": "I4",
        "scale": {
          "raw": "cm_or_deg*1e-7"
        },
        "description": "WGS84 ECEF Y coordinate or longitude, depending on flags above"
      },
      {
        "byte_offset": 12,
        "name": "ecefZOrAlt",
        "data_type": "I4",
        "unit": "cm",
        "description": "WGS84 ECEF Z coordinate or altitude, depending on flags above"
      },
      {
        "byte_offset": 16,
        "name": "fixedPosAcc",
        "data_type": "U4",
        "unit": "mm",
        "description": "Fixed position 3D accuracy"
      }
    ]
  }
}
