{
  "name": "UBX-MON-HW",
  "message_class": "0x0a",
  "message_id": "0x09",
  "message_type": "periodic_polled",
  "description": "Hardware status. Status of different aspects of the hardware, such as antenna, PIO/peripheral pins, noise level, automatic gain control (AGC).",
  "payload_length": {
    "fixed": 60
  },
  "deprecated": true,
  "deprecated_note": "This message is deprecated in this protocol version. Use UBX-MON-HW3 and UBX-MON-RF instead.",
  "payload_fields": [
    {
      "byte_offset": 0,
      "name": "pinSel",
      "data_type": "X4",
      "description": "Mask of pins set as peripheral/PIO"
    },
    {
      "byte_offset": 4,
      "name": "pinBank",
      "data_type": "X4",
      "description": "Mask of pins set as bank A/B"
    },
    {
      "byte_offset": 8,
      "name": "pinDir",
      "data_type": "X4",
      "description": "Mask of pins set as input/output"
    },
    {
      "byte_offset": 12,
      "name": "pinVal",
      "data_type": "X4",
      "description": "Mask of pins value low/high"
    },
    {
      "byte_offset": 16,
      "name": "noisePerMS",
      "data_type": "U2",
      "description": "Noise level as measured by the GPS core"
    },
    {
      "byte_offset": 18,
      "name": "agcCnt",
      "data_type": "U2",
      "description": "AGC monitor (counts SIGHI xor SIGLO, range 0 to 8191)"
    },
    {
      "byte_offset": 20,
      "name": "aStatus",
      "data_type": "U1",
      "description": "Status of the antenna supervisor state machine (0=INIT, 1=DONTKNOW, 2=OK, 3=SHORT, 4=OPEN)"
    },
    {
      "byte_offset": 21,
      "name": "aPower",
      "data_type": "U1",
      "description": "Current power status of antenna (0=OFF, 1=ON, 2=DONTKNOW)"
    },
    {
      "byte_offset": 22,
      "name": "flags",
      "data_type": "X1",
      "description": "Flags. Bit 0: rtcCalib (RTC is calibrated); Bit 1: safeBoot (Safeboot mode: 0=inactive, 1=active); Bits 3-2: jammingState (Jamming/interference monitor output: 0=unknown/disabled, 1=ok, 2=warning, 3=critical); Bit 4: xtalAbsent (RTC xtal absent)."
    },
    {
      "byte_offset": 23,
      "name": "reserved0",
      "data_type": "U1",
      "reserved": true,
      "description": "Reserved"
    },
    {
      "byte_offset": 24,
      "name": "usedMask",
      "data_type": "X4",
      "description": "Mask of pins that are used by the virtual pin manager"
    },
    {
      "byte_offset": 28,
      "name": "VP",
      "data_type": {
        "array_of": "U1",
        "count": 17
      },
      "description": "Array of pin mappings for each of the 17 physical pins"
    },
    {
      "byte_offset": 45,
      "name": "jamInd",
      "data_type": "U1",
      "description": "CW jamming indicator, scaled (0 = no CW jamming, 255 = strong CW jamming)"
    },
    {
      "byte_offset": 46,
      "name": "reserved1",
      "data_type": {
        "array_of": "U1",
        "count": 2
      },
      "reserved": true,
      "description": "Reserved"
    },
    {
      "byte_offset": 48,
      "name": "pinIrq",
      "data_type": "X4",
      "description": "Mask of pins value using the PIO Irq"
    },
    {
      "byte_offset": 52,
      "name": "pullH",
      "data_type": "X4",
      "description": "Mask of pins value using the PIO pull high resistor"
    },
    {
      "byte_offset": 56,
      "name": "pullL",
      "data_type": "X4",
      "description": "Mask of pins value using the PIO pull low resistor"
    }
  ]
}
