parts:
{
  "name": "UBX-MON-IO",
  "class_id": "0x0a",
  "message_id": "0x02",
  "description": "I/O system status",
  "message_type": "periodic_polled",
  "payload_length": {
    "variable": {
      "formula": "num_ports * 20"
    }
  },
  "payload": [
    {
      "name": "ports",
      "data_type": {
        "array_of": {
          "type": "struct",
          "fields": [
            {
              "byte_offset": 0,
              "name": "rxBytes",
              "data_type": "U4",
              "unit": "bytes",
              "description": "Number of bytes ever received"
            },
            {
              "byte_offset": 4,
              "name": "txBytes",
              "data_type": "U4",
              "unit": "bytes",
              "description": "Number of bytes ever sent"
            },
            {
              "byte_offset": 8,
              "name": "parityErrs",
              "data_type": "U2",
              "description": "Number of 100 ms timeslots with parity errors"
            },
            {
              "byte_offset": 10,
              "name": "framingErrs",
              "data_type": "U2",
              "description": "Number of 100 ms timeslots with framing errors"
            },
            {
              "byte_offset": 12,
              "name": "overrunErrs",
              "data_type": "U2",
              "description": "Number of 100 ms timeslots with overrun errors"
            },
            {
              "byte_offset": 14,
              "name": "breakCond",
              "data_type": "U2",
              "description": "Number of 100 ms timeslots with break conditions"
            },
            {
              "byte_offset": 16,
              "name": "reserved0",
              "data_type": {
                "array_of": "U1",
                "count": 4
              },
              "reserved": true,
              "description": "Reserved"
            }
          ]
        },
        "count": {
          "formula": "payload_length / 20"
        }
      }
    }
  ]
}
