parts:
{
  "message_id": "0x27",
  "class_id": "0x0a",
  "name": "UBX-MON-PATCH",
  "message_type": "polled",
  "payload_length": {
    "variable": {
      "base": 4,
      "formula": "4 + nEntries*16"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "version",
      "data_type": "U2",
      "description": "Message version (0x0001 for this version)",
      "fixed_value": 1
    },
    {
      "byte_offset": 2,
      "name": "nEntries",
      "data_type": "U2",
      "description": "Total number of reported patches"
    },
    {
      "byte_offset": 4,
      "name": "patches",
      "array_of": {
        "byte_length": 16,
        "fields": [
          {
            "byte_offset": 0,
            "name": "patchInfo",
            "data_type": "X4",
            "description": "Status information about the reported patch",
            "bitfields": [
              {
                "name": "activated",
                "data_type": "U1",
                "bit_offset": 0,
                "bit_length": 1,
                "description": "1: the patch is active, 0: otherwise"
              },
              {
                "name": "location",
                "data_type": "U1",
                "bit_offset": 1,
                "bit_length": 2,
                "description": "Indicates where the patch is stored. 0: eFuse, 1: ROM, 2: BBR, 3: file system"
              }
            ]
          },
          {
            "byte_offset": 4,
            "name": "comparatorNumber",
            "data_type": "U4",
            "description": "The number of the comparator"
          },
          {
            "byte_offset": 8,
            "name": "patchAddress",
            "data_type": "U4",
            "description": "The address that is targeted by the patch"
          },
          {
            "byte_offset": 12,
            "name": "patchData",
            "data_type": "U4",
            "description": "The data that is inserted at the patchAddress"
          }
        ]
      },
      "count_field": "nEntries"
    }
  ]
}
