{
  "ubx_message": {
    "name": "UBX-MON-RF",
    "description": "RF information",
    "class_id": "0x0a",
    "message_id": "0x38",
    "message_type": "periodic_polled",
    "payload_length": {
      "variable": {
        "base": 4,
        "formula": "4 + nBlocks * 24"
      }
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "data_type": "U1",
        "field_name": "version",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "data_type": "U1",
        "field_name": "nBlocks",
        "description": "The number of RF blocks included"
      },
      {
        "byte_offset": 2,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "field_name": "reserved0",
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 4,
        "field_name": "rfBlocks",
        "description": "Information for each RF block",
        "repeated_group": {
          "count_field_name": "nBlocks",
          "block_length": 24,
          "fields": [
            {
              "byte_offset": 0,
              "data_type": "U1",
              "field_name": "blockId",
              "description": "RF block ID (0 = L1 band, 1 = L2 or L5 band depending on product configuration)"
            },
            {
              "byte_offset": 1,
              "data_type": "X1",
              "field_name": "flags",
              "description": "Flags (bits 1...0 U:2 jammingState - output from Jamming/Interference Monitor (0 = unknown or feature disabled, 1 = ok no significant jamming, 2 = warning - interference visible but fix OK, 3 = critical - interference visible and no fix))"
            },
            {
              "byte_offset": 2,
              "data_type": "U1",
              "field_name": "antStatus",
              "description": "Status of the antenna supervisor state machine (0x00=INIT, 0x01=DONTKNOW, 0x02=OK, 0x03=SHORT, 0x04=OPEN)"
            },
            {
              "byte_offset": 3,
              "data_type": "U1",
              "field_name": "antPower",
              "description": "Current power status of antenna (0x00=OFF, 0x01=ON, 0x02=DONTKNOW)"
            },
            {
              "byte_offset": 4,
              "data_type": "U4",
              "field_name": "postStatus",
              "description": "POST status word"
            },
            {
              "byte_offset": 8,
              "data_type": {
                "array_of": "U1",
                "count": 4
              },
              "field_name": "reserved1",
              "description": "Reserved",
              "reserved": true
            },
            {
              "byte_offset": 12,
              "data_type": "U2",
              "field_name": "noisePerMS",
              "description": "Noise level as measured by the GPS core"
            },
            {
              "byte_offset": 14,
              "data_type": "U2",
              "field_name": "agcCnt",
              "description": "AGC Monitor (counts SIGHI xor SIGLO, range 0 to 8191)"
            },
            {
              "byte_offset": 16,
              "data_type": "U1",
              "field_name": "jamInd",
              "description": "CW jamming indicator, scaled (0=no CW jamming, 255 = strong CW jamming)"
            },
            {
              "byte_offset": 17,
              "data_type": "I1",
              "field_name": "ofsI",
              "description": "Imbalance of I-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
            },
            {
              "byte_offset": 18,
              "data_type": "U1",
              "field_name": "magI",
              "description": "Magnitude of I-part of complex signal, scaled (0 = no signal, 255 = max.magnitude)"
            },
            {
              "byte_offset": 19,
              "data_type": "I1",
              "field_name": "ofsQ",
              "description": "Imbalance of Q-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
            },
            {
              "byte_offset": 20,
              "data_type": "U1",
              "field_name": "magQ",
              "description": "Magnitude of Q-part of complex signal, scaled (0 = no signal, 255 = max.magnitude)"
            },
            {
              "byte_offset": 21,
              "data_type": {
                "array_of": "U1",
                "count": 3
              },
              "field_name": "reserved2",
              "description": "Reserved",
              "reserved": true
            }
          ]
        }
      }
    ]
  }
}
