{
  "name": "UBX-MON-SPAN",
  "class_id": "0x0a",
  "message_id": "0x31",
  "description": "Signal characteristics. This message is to be used as a basic spectrum analyzer, where it displays one spectrum for each of the receiver's existing RF paths. The spectrum is conveyed with the following parameters: The frequency span in Hz, the frequency bin resolution in Hz, the center frequency in Hz, and 256 bins with amplitude data. Additionally, in order to give further insight on the signal captured by the receiver, the current gain of the internal programmable gain amplifier (PGA) is provided.",
  "message_type": "periodic_polled",
  "payload_length": {
    "variable": {
      "base": 4,
      "formula": "4 + numRfBlocks * 272"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "version",
      "data_type": "U1",
      "description": "Message version (0x00 for this version)",
      "fixed_value": 0
    },
    {
      "byte_offset": 1,
      "name": "numRfBlocks",
      "data_type": "U1",
      "description": "Number of RF blocks included"
    },
    {
      "byte_offset": 2,
      "name": "reserved0",
      "array_of": "U1",
      "count": 2,
      "description": "Reserved",
      "reserved": true
    },
    {
      "byte_offset": 4,
      "name": "block",
      "description": "Repeated block for each RF block",
      "repeats": {
        "count_field": "numRfBlocks"
      },
      "fields": [
        {
          "byte_offset": 0,
          "name": "spectrum",
          "array_of": "U1",
          "count": 256,
          "unit": "dB",
          "description": "Spectrum data (number of points = span/res)"
        },
        {
          "byte_offset": 256,
          "name": "span",
          "data_type": "U4",
          "unit": "Hz",
          "description": "Spectrum span"
        },
        {
          "byte_offset": 260,
          "name": "res",
          "data_type": "U4",
          "unit": "Hz",
          "description": "Resolution of the spectrum"
        },
        {
          "byte_offset": 264,
          "name": "center",
          "data_type": "U4",
          "unit": "Hz",
          "description": "Center of spectrum span"
        },
        {
          "byte_offset": 268,
          "name": "pga",
          "data_type": "U1",
          "unit": "dB",
          "description": "Programmable gain amplifier"
        },
        {
          "byte_offset": 269,
          "name": "reserved1",
          "array_of": "U1",
          "count": 3,
          "description": "Reserved",
          "reserved": true
        }
      ]
    }
  ]
}
