parts:
{
  "name": "UBX-NAV-COV",
  "class_id": "0x01",
  "message_id": "0x36",
  "message_type": "periodic_polled",
  "description": "This message outputs the covariance matrices for the position and velocity solutions in the topocentric coordinate system defined as the local-level North (N), East (E), Down (D) frame. As the covariance matrices are symmetric, only the upper triangular part is output.",
  "payload_length": {
    "fixed": 64
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "iTOW",
      "data_type": "U4",
      "unit": "ms",
      "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details."
    },
    {
      "byte_offset": 4,
      "name": "version",
      "data_type": "U1",
      "fixed_value": 0,
      "description": "Message version (0x00 for this version)"
    },
    {
      "byte_offset": 5,
      "name": "posCovValid",
      "data_type": "U1",
      "description": "Position covariance matrix validity flag"
    },
    {
      "byte_offset": 6,
      "name": "velCovValid",
      "data_type": "U1",
      "description": "Velocity covariance matrix validity flag"
    },
    {
      "byte_offset": 7,
      "name": "reserved0",
      "data_type": {
        "array_of": "U1",
        "count": 9
      },
      "reserved": true,
      "description": "Reserved"
    },
    {
      "byte_offset": 16,
      "name": "posCoVNN",
      "data_type": "R4",
      "unit": "m^2",
      "description": "Position covariance matrix value p_NN"
    },
    {
      "byte_offset": 20,
      "name": "posCOVNE",
      "data_type": "R4",
      "unit": "m^2",
      "description": "Position covariance matrix value p_NE"
    },
    {
      "byte_offset": 24,
      "name": "posCoVND",
      "data_type": "R4",
      "unit": "m^2",
      "description": "Position covariance matrix value p_ND"
    },
    {
      "byte_offset": 28,
      "name": "posCovEE",
      "data_type": "R4",
      "unit": "m^2",
      "description": "Position covariance matrix value p_EE"
    },
    {
      "byte_offset": 32,
      "name": "posCOVED",
      "data_type": "R4",
      "unit": "m^2",
      "description": "Position covariance matrix value p_ED"
    },
    {
      "byte_offset": 36,
      "name": "posCovDD",
      "data_type": "R4",
      "unit": "m^2",
      "description": "Position covariance matrix value p_DD"
    },
    {
      "byte_offset": 40,
      "name": "velCovNN",
      "data_type": "R4",
      "unit": "m^2/s^2",
      "description": "Velocity covariance matrix value v_NN"
    },
    {
      "byte_offset": 44,
      "name": "velCovNE",
      "data_type": "R4",
      "unit": "m^2/s^2",
      "description": "Velocity covariance matrix value v_NE"
    },
    {
      "byte_offset": 48,
      "name": "velCovND",
      "data_type": "R4",
      "unit": "m^2/s^2",
      "description": "Velocity covariance matrix value v_ND"
    },
    {
      "byte_offset": 52,
      "name": "velCovEE",
      "data_type": "R4",
      "unit": "m^2/s^2",
      "description": "Velocity covariance matrix value v_EE"
    },
    {
      "byte_offset": 56,
      "name": "velCovED",
      "data_type": "R4",
      "unit": "m^2/s^2",
      "description": "Velocity covariance matrix value v_ED"
    },
    {
      "byte_offset": 60,
      "name": "velCovDD",
      "data_type": "R4",
      "unit": "m^2/s^2",
      "description": "Velocity covariance matrix value v_DD"
    }
  ]
}
