parts:
{
  "class_id": 1,
  "message_id": 67,
  "message_name": "UBX-NAV-SIG",
  "message_type": "periodic_polled",
  "description": "Signal information",
  "payload_length": {
    "variable": {
      "base": 8,
      "formula": "8 + N*16",
      "parameter": "numSigs"
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U4",
      "name": "iTOW",
      "unit": "ms",
      "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details."
    },
    {
      "byte_offset": 4,
      "data_type": "U1",
      "name": "version",
      "fixed_value": 0,
      "description": "Message version (0x00 for this version)"
    },
    {
      "byte_offset": 5,
      "data_type": "U1",
      "name": "numSigs",
      "description": "Number of signals"
    },
    {
      "byte_offset": 6,
      "data_type": {
        "array_of": "U1",
        "count": 2
      },
      "name": "reserved0",
      "reserved": true,
      "description": "Reserved"
    },
    {
      "byte_offset": 8,
      "data_type": {
        "array_of": {
          "fields": [
            {
              "byte_offset": 0,
              "data_type": "U1",
              "name": "gnssId",
              "description": "GNSS identifier (see Satellite Numbering) for assignment"
            },
            {
              "byte_offset": 1,
              "data_type": "U1",
              "name": "svId",
              "description": "Satellite identifier (see Satellite Numbering) for assignment"
            },
            {
              "byte_offset": 2,
              "data_type": "U1",
              "name": "sigId",
              "description": "New style signal identifier (see Signal Identifiers)"
            },
            {
              "byte_offset": 3,
              "data_type": "U1",
              "name": "freqId",
              "description": "Only used for GLONASS: This is the frequency slot + 7 (range from 0 to 13)"
            },
            {
              "byte_offset": 4,
              "data_type": "I2",
              "name": "prRes",
              "scale": {
                "multiplier": 0.1,
                "raw_string": "0.1"
              },
              "unit": "m",
              "description": "Pseudorange residual"
            },
            {
              "byte_offset": 6,
              "data_type": "U1",
              "name": "cno",
              "unit": "dBHz",
              "description": "Carrier-to-noise density ratio (signal strength)"
            },
            {
              "byte_offset": 7,
              "data_type": "U1",
              "name": "qualityInd",
              "description": "Signal quality indicator: \u2022 0 = no signal \u2022 1 = searching signal \u2022 2 = signal acquired \u2022 3 = signal detected but unusable \u2022 4 = code locked and time synchronized \u2022 5, 6, 7 = code and carrier locked and time synchronized"
            },
            {
              "byte_offset": 8,
              "data_type": "U1",
              "name": "corrSource",
              "description": "Correction source: \u2022 0 = no corrections \u2022 1 = SBAS corrections \u2022 2 = BeiDou corrections \u2022 3 = RTCM2 corrections \u2022 4 = RTCM3 OSR corrections \u2022 5 = RTCM3 SSR corrections \u2022 6 = QZSS SLAS corrections \u2022 7 = SPARTN corrections \u2022 8 = CLAS corrections"
            },
            {
              "byte_offset": 9,
              "data_type": "U1",
              "name": "ionoModel",
              "description": "Ionospheric model used: \u2022 0 = no model \u2022 1 = Klobuchar model transmitted by GPS \u2022 2 = SBAS model \u2022 3 = Klobuchar model transmitted by BeiDou \u2022 8 = Iono delay derived from dual frequency observations"
            },
            {
              "byte_offset": 10,
              "data_type": "X2",
              "name": "sigFlags",
              "description": "Signal related flags",
              "bitfields": [
                {
                  "bit_offset": 0,
                  "num_bits": 2,
                  "name": "health",
                  "description": "Signal health flag: \u2022 0 = unknown \u2022 1 = healthy \u2022 2 = unhealthy"
                },
                {
                  "bit_offset": 2,
                  "num_bits": 1,
                  "name": "prSmoothed",
                  "description": "1 = Pseudorange has been smoothed"
                },
                {
                  "bit_offset": 3,
                  "num_bits": 1,
                  "name": "prUsed",
                  "description": "1 = Pseudorange has been used for this signal"
                },
                {
                  "bit_offset": 4,
                  "num_bits": 1,
                  "name": "crUsed",
                  "description": "1 = Carrier range has been used for this signal"
                },
                {
                  "bit_offset": 5,
                  "num_bits": 1,
                  "name": "doUsed",
                  "description": "1 = Range rate (Doppler) has been used for this signal"
                }
              ]
            },
            {
              "byte_offset": 12,
              "data_type": {
                "array_of": "U1",
                "count": 4
              },
              "name": "reserved1",
              "reserved": true,
              "description": "Reserved"
            }
          ],
          "byte_length": 16
        },
        "count": "numSigs"
      },
      "name": "repeated_signals",
      "description": "Information for each tracked signal"
    }
  ]
}
