{
  "ubx_message": {
    "message_name": "UBX-NAV-TIMEBDS",
    "class_id": "0x01",
    "message_id": "0x24",
    "description": "BeiDou time solution",
    "message_type": "periodic_polled",
    "comment": "This message reports the precise BDS time of the most recent navigation solution including validity flags and an accuracy estimate.",
    "payload_length": {
      "fixed": 20
    },
    "payload_fields": [
      {
        "byte_offset": 0,
        "name": "iTOW",
        "data_type": "U4",
        "unit": "ms",
        "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details."
      },
      {
        "byte_offset": 4,
        "name": "SOW",
        "data_type": "U4",
        "unit": "s",
        "description": "BDS time of week (rounded to seconds)"
      },
      {
        "byte_offset": 8,
        "name": "fSOW",
        "data_type": "I4",
        "unit": "ns",
        "description": "Fractional part of SOW (range: +/-500000000). The precise BDS time of week in seconds is: SOW + fSOW * 1e-9"
      },
      {
        "byte_offset": 12,
        "name": "week",
        "data_type": "I2",
        "description": "BDS week number of the navigation epoch"
      },
      {
        "byte_offset": 14,
        "name": "leaps",
        "data_type": "I1",
        "unit": "s",
        "description": "BDS leap seconds (BDS-UTC)"
      },
      {
        "byte_offset": 15,
        "name": "valid",
        "data_type": "X1",
        "description": "Validity Flags",
        "bit_fields": [
          {
            "name": "sowValid",
            "data_type": "U1",
            "bit_offset": 0,
            "bit_length": 1,
            "description": "1 = Valid SOW and fSOW (see section Time validity in the integration manual for details)"
          },
          {
            "name": "weekValid",
            "data_type": "U1",
            "bit_offset": 1,
            "bit_length": 1,
            "description": "1 = Valid week (see section Time validity in the integration manual for details)"
          },
          {
            "name": "leapSValid",
            "data_type": "U1",
            "bit_offset": 2,
            "bit_length": 1,
            "description": "1 = Valid leap second"
          }
        ]
      },
      {
        "byte_offset": 16,
        "name": "tAcc",
        "data_type": "U4",
        "unit": "ns",
        "description": "Time Accuracy Estimate"
      }
    ]
  }
}
