parts:
{
  "ubx_message": {
    "name": "UBX-RXM-MEASX",
    "class": "0x02",
    "id": "0x14",
    "description": "Satellite measurements for RRLP",
    "payload_length_formula": "44 + numSV * 24",
    "fields": [
      {
        "name": "version",
        "type": "U1",
        "offset": 0,
        "description": "Message version, currently 0x01",
        "value": "0x01"
      },
      {
        "name": "reserved0",
        "type": "U1[3]",
        "offset": 1,
        "description": "Reserved"
      },
      {
        "name": "gpsTOW",
        "type": "U4",
        "offset": 4,
        "unit": "ms",
        "description": "GPS measurement reference time"
      },
      {
        "name": "gloTOW",
        "type": "U4",
        "offset": 8,
        "unit": "ms",
        "description": "GLONASS measurement reference time"
      },
      {
        "name": "bdsTOW",
        "type": "U4",
        "offset": 12,
        "unit": "ms",
        "description": "BeiDou measurement reference time"
      },
      {
        "name": "reserved1",
        "type": "U1[4]",
        "offset": 16,
        "description": "Reserved"
      },
      {
        "name": "qzssTOW",
        "type": "U4",
        "offset": 20,
        "unit": "ms",
        "description": "QZSS measurement reference time"
      },
      {
        "name": "gpsTOWAcc",
        "type": "U2",
        "offset": 24,
        "scale": "2^-4",
        "unit": "ms",
        "description": "GPS measurement reference time accuracy (0xffff = > 4s)"
      },
      {
        "name": "gloTOWAcc",
        "type": "U2",
        "offset": 26,
        "scale": "2^-4",
        "unit": "ms",
        "description": "GLONASS measurement reference time accuracy (0xffff = > 4s)"
      },
      {
        "name": "bdsTOWAcc",
        "type": "U2",
        "offset": 28,
        "scale": "2^-4",
        "unit": "ms",
        "description": "BeiDou measurement reference time accuracy (0xffff = > 4s)"
      },
      {
        "name": "reserved2",
        "type": "U1[2]",
        "offset": 30,
        "description": "Reserved"
      },
      {
        "name": "qzssTOWAcc",
        "type": "U2",
        "offset": 32,
        "scale": "2^-4",
        "unit": "ms",
        "description": "QZSS measurement reference time accuracy (0xffff = > 4s)"
      },
      {
        "name": "numSV",
        "type": "U1",
        "offset": 34,
        "description": "Number of satellites in repeated block"
      },
      {
        "name": "flags",
        "type": "X1",
        "offset": 35,
        "description": "Flags",
        "fields": [
          {
            "name": "towSet",
            "type": "U:2",
            "bits": "1:0",
            "description": "TOW set (0 = no, 1 or 2 = yes)"
          }
        ]
      },
      {
        "name": "reserved3",
        "type": "U1[8]",
        "offset": 36,
        "description": "Reserved"
      },
      {
        "name": "repeated_block",
        "type": "block",
        "offset": 44,
        "num_repeats_field": "numSV",
        "block_length": 24,
        "fields": [
          {
            "name": "gnssId",
            "type": "U1",
            "offset_in_block": 0,
            "description": "GNSS ID (see Satellite Numbering)"
          },
          {
            "name": "svId",
            "type": "U1",
            "offset_in_block": 1,
            "description": "Satellite ID (see Satellite Numbering)"
          },
          {
            "name": "CNo",
            "type": "U1",
            "offset_in_block": 2,
            "description": "carrier noise ratio (0..63)"
          },
          {
            "name": "mpathIndic",
            "type": "U1",
            "offset_in_block": 3,
            "description": "multipath index (according to [1]) (0 = not measured, 1 = low, 2 = medium, 3 = high)"
          },
          {
            "name": "dopplerMS",
            "type": "I4",
            "offset_in_block": 4,
            "scale": "0.04",
            "unit": "m/s",
            "description": "Doppler measurement"
          },
          {
            "name": "dopplerHz",
            "type": "I4",
            "offset_in_block": 8,
            "scale": "0.2",
            "unit": "Hz",
            "description": "Doppler measurement"
          },
          {
            "name": "wholeChips",
            "type": "U2",
            "offset_in_block": 12,
            "description": "whole value of the code phase measurement (0..1022 for GPS)"
          },
          {
            "name": "fracChips",
            "type": "U2",
            "offset_in_block": 14,
            "description": "fractional value of the code phase measurement (0..1023)"
          },
          {
            "name": "codePhase",
            "type": "U4",
            "offset_in_block": 16,
            "scale": "2^-21",
            "unit": "ms",
            "description": "Code phase"
          },
          {
            "name": "intCodePhase",
            "type": "U1",
            "offset_in_block": 20,
            "unit": "ms",
            "description": "Integer (part of the) code phase"
          },
          {
            "name": "pseuRangeRMS",
            "type": "U1",
            "offset_in_block": 21,
            "description": "pseudorange RMS error index (according to [1]) (0..63)"
          },
          {
            "name": "reserved4",
            "type": "U1[2]",
            "offset_in_block": 22,
            "description": "Reserved"
          }
        ]
      }
    ]
  }
}
