parts:
{
  "name": "UBX-TIM-TP",
  "id": {
    "class_id": 13,
    "message_id": 1
  },
  "message_type": "periodic_polled",
  "description": "This message contains information on the timing of the next pulse at the TIMEPULSEO output. The recommended configuration when using this message is to set both the measurement rate (CFG-RATE) and the timepulse frequency (CFG-TP) to 1 Hz.",
  "payload_length": {
    "fixed": 16
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "towMS",
      "data_type": "U4",
      "unit": "ms",
      "description": "Time pulse time of week according to time base"
    },
    {
      "byte_offset": 4,
      "name": "towSubMS",
      "data_type": "U4",
      "scale": {
        "raw": "2^-32",
        "multiplier": 2.3283064365386963e-10
      },
      "unit": "ms",
      "description": "Submillisecond part of towMS"
    },
    {
      "byte_offset": 8,
      "name": "qErr",
      "data_type": "I4",
      "unit": "ps",
      "description": "Quantization error of time pulse"
    },
    {
      "byte_offset": 12,
      "name": "week",
      "data_type": "U2",
      "unit": "weeks",
      "description": "Time pulse week number according to time base"
    },
    {
      "byte_offset": 14,
      "name": "flags",
      "data_type": "X1",
      "description": "Flags",
      "bit_fields": [
        {
          "bit_offset": 0,
          "bit_length": 1,
          "name": "timeBase",
          "description": "Time base",
          "value_description": [
            {
              "value": 0,
              "description": "Time base is GNSS"
            },
            {
              "value": 1,
              "description": "Time base is UTC"
            }
          ]
        },
        {
          "bit_offset": 1,
          "bit_length": 1,
          "name": "utc",
          "description": "UTC available",
          "value_description": [
            {
              "value": 0,
              "description": "UTC not available"
            },
            {
              "value": 1,
              "description": "UTC available"
            }
          ]
        },
        {
          "bit_offset": 2,
          "bit_length": 2,
          "name": "raim",
          "description": "(T)RAIM information",
          "value_description": [
            {
              "value": 0,
              "description": "Information not available"
            },
            {
              "value": 1,
              "description": "Not active"
            }
          ]
        },
        {
          "bit_offset": 4,
          "bit_length": 1,
          "name": "qErrInvalid",
          "description": "Quantization Error Invalid Flag",
          "value_description": [
            {
              "value": 0,
              "description": "Quantization error valid"
            },
            {
              "value": 1,
              "description": "Quantization error invalid"
            }
          ]
        }
      ]
    },
    {
      "byte_offset": 15,
      "name": "refInfo",
      "data_type": "X1",
      "description": "Time reference information"
    }
  ]
}
