parts:
{
  "class_id": "0x06",
  "message_id": "0x00",
  "name": "UBX-CFG-PRT",
  "message_type": "input_output",
  "type": "get_set",
  "description": "This polymorphic message configures various port types (UART, USB, SPI, I2C). The payload structure for offsets 4-11 (mode and baudRate/reserved fields) varies significantly based on the 'portID' field. This definition primarily describes the structure for UART ports for fields 4-11, with notes on variations. Additionally, a distinct 'Poll request' variant of this message exists with a fixed payload length of 1 byte, containing only a U1 'portID' at offset 0, used to request a port's configuration.",
  "payload_length": {
    "variable": {
      "formula": "20 * N",
      "min_N": 1,
      "description": "N configuration units. Input messages can concatenate units. Output messages always contain one config unit (N=1), thus having a fixed length of 20 bytes."
    }
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "portID",
      "description": "Port identifier number. (e.g., 0 for I2C, 1 for UART1, 2 for UART2, 3 for USB, 4 for SPI)"
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "reserved0",
      "reserved": true
    },
    {
      "byte_offset": 2,
      "data_type": "X2",
      "name": "txReady",
      "description": "Enable TX ready feature for this port. This field's interpretation of its bits is common across UART, USB, SPI, I2C ports.",
      "bitfields": [
        {
          "bits": "0",
          "data_type": "U:1",
          "name": "en",
          "description": "Enable TX ready feature for this port"
        },
        {
          "bits": "1",
          "data_type": "U:1",
          "name": "pol",
          "description": "Polarity (0: High-active, 1: Low-active)"
        },
        {
          "bits": "15:2",
          "data_type": "U:14",
          "name": "reserved_txReady",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 4,
      "data_type": "X4",
      "name": "mode",
      "description": "A bit mask describing the UART mode. NOTE: This field (and subsequent 'baudRate' field) is specific to UART port configurations. For USB, this 8-byte block (offsets 4-11) is U1[8] reserved. For SPI and I2C, this block contains an X4 'mode' field (with different bitfields, e.g., spiMode for SPI, slaveAddr for I2C) and a U1[4] reserved field.",
      "bitfields": [
        {
          "bits": "5:0",
          "data_type": "U:6",
          "name": "reserved_mode_0_5",
          "reserved": true
        },
        {
          "bits": "7:6",
          "data_type": "U:2",
          "name": "charLen",
          "description": "Character length (00: 5bit (not supported), 01: 6bit (not supported), 10: 7bit (supported only with parity), 11: 8bit)"
        },
        {
          "bits": "8",
          "data_type": "U:1",
          "name": "reserved_mode_8",
          "reserved": true
        },
        {
          "bits": "11:9",
          "data_type": "U:3",
          "name": "parity",
          "description": "Parity (000: Even parity, 001: Odd parity, 10X: No parity, X1X: Reserved)"
        },
        {
          "bits": "13:12",
          "data_type": "U:2",
          "name": "nStopBits",
          "description": "Number of Stop bits (00: 1 Stop bit, 01: 1.5 Stop bit, 10: 2 Stop bit, 11: 0.5 Stop bit)"
        },
        {
          "bits": "31:14",
          "data_type": "U:18",
          "name": "reserved_mode_14_31",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 8,
      "data_type": "U4",
      "name": "baudRate",
      "unit": "Bits/s",
      "description": "Baud rate in bits/second. NOTE: This field is specific to UART port configurations. For other port types, this memory region is used differently (e.g., reserved or part of other port-specific modes)."
    },
    {
      "byte_offset": 12,
      "data_type": "X2",
      "name": "inProtoMask",
      "description": "A mask describing which input protocols are active. Each bit of this mask is used for a protocol. Common across UART, USB, SPI, I2C ports.",
      "bitfields": [
        {
          "bits": "0",
          "data_type": "U:1",
          "name": "inUbx",
          "description": "UBX protocol"
        },
        {
          "bits": "1",
          "data_type": "U:1",
          "name": "inNmea",
          "description": "NMEA protocol"
        },
        {
          "bits": "2",
          "data_type": "U:1",
          "name": "inRtcm",
          "description": "RTCM2 protocol"
        },
        {
          "bits": "4:3",
          "data_type": "U:2",
          "name": "reserved_inProtoMask_3_4",
          "reserved": true
        },
        {
          "bits": "5",
          "data_type": "U:1",
          "name": "inRtcm3",
          "description": "RTCM3 protocol (not supported for protocol versions less than 20.00)"
        },
        {
          "bits": "15:6",
          "data_type": "U:10",
          "name": "reserved_inProtoMask_6_15",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 14,
      "data_type": "X2",
      "name": "outProtoMask",
      "description": "A mask describing which output protocols are active. Each bit of this mask is used for a protocol. Common across UART, USB, SPI, I2C ports.",
      "bitfields": [
        {
          "bits": "0",
          "data_type": "U:1",
          "name": "outUbx",
          "description": "UBX protocol"
        },
        {
          "bits": "1",
          "data_type": "U:1",
          "name": "outNmea",
          "description": "NMEA protocol"
        },
        {
          "bits": "4:2",
          "data_type": "U:3",
          "name": "reserved_outProtoMask_2_4",
          "reserved": true
        },
        {
          "bits": "5",
          "data_type": "U:1",
          "name": "outRtcm3",
          "description": "RTCM3 protocol (not supported for protocol versions less than 20.00)"
        },
        {
          "bits": "15:6",
          "data_type": "U:10",
          "name": "reserved_outProtoMask_6_15",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 16,
      "data_type": "X2",
      "name": "flags",
      "description": "Flags bit mask. Common across UART, USB, SPI, I2C ports. Bit 1, 'extendedTx', affects TX ready functionality.",
      "bitfields": [
        {
          "bits": "0",
          "data_type": "U:1",
          "name": "reserved_flags_0",
          "reserved": true
        },
        {
          "bits": "1",
          "data_type": "U:1",
          "name": "extendedTx",
          "description": "Extended TX timeout: if set, the port will time out if allocated TX memory >=4 kB and no activity for 1.5 s. If not set, the port will time out if no activity for 1.5 s regardless of allocated TX memory (not supported for protocol versions less than 13.01)."
        },
        {
          "bits": "15:2",
          "data_type": "U:14",
          "name": "reserved_flags_2_15",
          "reserved": true
        }
      ]
    },
    {
      "byte_offset": 18,
      "data_type": "U1",
      "name": "reserved_final",
      "array_of": "U1",
      "count": 2,
      "reserved": true,
      "description": "Reserved. (Named 'reserved1' or 'reserved2' in some port definitions, e.g., USB, SPI, I2C)."
    }
  ]
}
