{
  "message_name": "UBX-MGA-INI-TIME_UTC",
  "class_id": "0x13",
  "message_id": "0x40",
  "message_type": "input",
  "payload_length": {
    "fixed": 24
  },
  "payload_fields": [
    {
      "byte_offset": 0,
      "name": "type",
      "data_type": "U1",
      "fixed_value": 16,
      "description": "Message type (0x10 for this type)"
    },
    {
      "byte_offset": 1,
      "name": "version",
      "data_type": "U1",
      "fixed_value": 0,
      "description": "Message version (0x00 for this version)"
    },
    {
      "byte_offset": 2,
      "name": "ref",
      "data_type": "X1",
      "description": "Reference to be used to set time",
      "bitfield": [
        {
          "name": "source",
          "position": 0,
          "length": 4,
          "description": "Source of time reference",
          "value_map": {
            "0": "none, i.e. on receipt of message (will be inaccurate!)",
            "1": "relative to pulse sent to EXTINT0",
            "2": "relative to pulse sent to EXTINT1",
            "3-15": "reserved"
          }
        },
        {
          "name": "fall",
          "position": 4,
          "length": 1,
          "description": "use falling edge of EXTINT pulse (default rising) - only if source is EXTINT"
        },
        {
          "name": "last",
          "position": 5,
          "length": 1,
          "description": "use last EXTINT pulse (default next pulse) - only if source is EXTINT"
        }
      ]
    },
    {
      "byte_offset": 3,
      "name": "leapSecs",
      "data_type": "I1",
      "unit": "S",
      "description": "Number of leap seconds since 1980 (or 0x80 = -128 if unknown)"
    },
    {
      "byte_offset": 4,
      "name": "year",
      "data_type": "U2",
      "description": "Year"
    },
    {
