{
  "message_name": "UBX-MON-COMMS",
  "class_id": "0x0a",
  "message_id": "0x36",
  "message_type": "periodic_polled",
  "comment": "Consolidated communications information for all ports. The size of the message is determined by the number of ports that are in use on the receiver. A port is only included if communication, either send or receive, has been initiated on that port.",
  "payload_length": {
    "variable": {
      "base": 8,
      "formula": "8 + nPorts * 40"
    }
  },
  "payload_fields": [
    {
      "byte_offset": 0,
      "data_type": "U1",
      "name": "version",
      "description": "Message version (0x00 for this type)",
      "fixed_value": 0
    },
    {
      "byte_offset": 1,
      "data_type": "U1",
      "name": "nPorts",
      "description": "Number of ports included"
    },
    {
      "byte_offset": 2,
      "data_type": "X1",
      "name": "txErrors",
      "description": "TX error bitmask. Bit 0: Memory Allocation error. Bit 1: Allocation error (TX buffer full). Bits 4-2: Output port (0 = N/A)."
    },
    {
      "byte_offset": 3,
      "data_type": {
        "array_of": "U1",
        "count": 5
      },
      "name": "reserved0",
      "reserved": true
    },
    {
      "name": "ports",
      "count_field": "nPorts",
      "element_length": 40,
      "fields": [
        {
          "byte_offset": 0,
          "data_type": "U2",
          "name": "portId",
          "description": "Unique identifier for the port. See section Communications ports in the integration manual for details."
        },
        {
          "byte_offset": 2,
          "data_type": "U2",
          "name": "txPending",
          "unit": "bytes",
          "description": "Number of bytes pending in transmitter buffer"
        },
        {
          "byte_offset": 4,
          "data_type": "U4",
          "name": "txBytes",
          "unit": "bytes",
          "description": "Number of bytes ever sent"
        },
        {
          "byte_offset": 8,
          "data_type": "U1",
          "name": "txUsage",
          "unit": "%",
          "description": "Maximum usage transmitter buffer during the last sysmon period"
        },
        {
          "byte_offset": 9,
          "data_type": "U1",
          "name": "txPeakUsage",
          "unit": "%",
          "description": "Maximum usage transmitter buffer"
        },
        {
          "byte_offset": 10,
          "data_type": "U2",
          "name": "rxPending",
          "unit": "bytes",
          "description": "Number of bytes in receiver buffer"
        },
        {
          "byte_offset": 12,
          "data_type": "U4",
          "name": "rxBytes",
          "unit": "bytes",
          "description": "Number of bytes ever received"
        },
        {
          "byte_offset": 16,
          "data_type": "U1",
          "name": "rxUsage",
          "unit": "%",
          "description": "Maximum usage receiver buffer during the last sysmon period"
        },
        {
          "byte_offset": 17,
          "data_type": "U1",
          "name": "rxPeakUsage",
          "unit": "%",
          "description": "Maximum usage receiver buffer"
        },
        {
          "byte_offset": 18,
          "data_type": "U2",
          "name": "overrunErrs",
          "description": "Number of 100 ms timeslots with overrun errors"
        },
        {
          "byte_offset": 20,
          "data_type": {
            "array_of": "U2",
            "count": 4
          },
          "name": "msgs",
          "unit": "msg",
          "description": "Number of successfully parsed messages for each protocol. The reported protocols are identified through the protIds field."
        },
        {
          "byte_offset": 28,
          "data_type": {
            "array_of": "U1",
            "count": 8
          },
          "name": "reserved1",
          "reserved": true
        },
        {
          "byte_offset": 36,
          "data_type": "U4",
          "name": "skipped",
          "unit": "bytes",
          "description": "Number of skipped bytes"
        }
      ]
    }
  ]
}
