{
  "ubx_message": {
    "message_class": "0x0a",
    "message_id": "0x0b",
    "message_name": "UBX-MON-HW2",
    "message_type": "periodic_polled",
    "description": "Extended hardware status",
    "comment": "This message is deprecated in this protocol version. Use UBX-MON-HW3 and UBX-MON-RF instead. Status of different aspects of the hardware such as Imbalance, Low-Level Configuration and POST Results. The first four parameters of this message represent the complex signal from the RF front end. The following rules of thumb apply: • The smaller the absolute value of the variable ofsI and ofsQ, the better. • Ideally, the magnitude of the I-part (magI) and the Q-part (magQ) of the complex signal should be the same.",
    "payload_length": {
      "fixed": 28
    },
    "fields": [
      {
        "byte_offset": 0,
        "name": "ofsI",
        "data_type": "I1",
        "description": "Imbalance of I-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
      },
      {
        "byte_offset": 1,
        "name": "magI",
        "data_type": "U1",
        "description": "Magnitude of I-part of complex signal, scaled (0 = no signal, 255 = max. magnitude)"
      },
      {
        "byte_offset": 2,
        "name": "ofsQ",
        "data_type": "I1",
        "description": "Imbalance of Q-part of complex signal, scaled (-128 = max. negative imbalance, 127 = max. positive imbalance)"
      },
      {
        "byte_offset": 3,
        "name": "magQ",
        "data_type": "U1",
        "description": "Magnitude of Q-part of complex signal, scaled (0 = no signal, 255 = max. magnitude)"
      },
      {
        "byte_offset": 4,
        "name": "cfgSource",
        "data_type": "U1",
        "description": "Source of low-level configuration (114 = ROM, 111 = OTP, 112 = config pins, 102 = flash image)"
      },
      {
        "byte_offset": 5,
        "name": "reserved0",
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 8,
        "name": "lowLevCfg",
        "data_type": "U4",
        "description": "Low-level configuration (obsolete for protocol versions greater than 15.00)"
      },
      {
        "byte_offset": 12,
        "name": "reserved1",
        "data_type": {
          "array_of": "U1",
          "count": 8
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "byte_offset": 20,
        "name": "postStatus",
        "data_type": "U4",
        "description": "POST status word"
      },
      {
        "byte_offset": 24,
        "name": "reserved2",
        "data_type": {
          "array_of": "U1",
          "count": 4
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}
