parts:
{
  "ubx_message": {
    "message_name": "UBX-MON-HW3",
    "class_id": 10,
    "message_id": 55,
    "message_type": "periodic_polled",
    "comment": "This message contains information specific to each HW I/O pin, for example whether the pin is set as Input or Output. For the antenna supervisor status and other RF status information, see the UBX-MON-RF message.",
    "payload_length": {
      "variable": {
        "base": 22,
        "formula": "22 + nPins * 6"
      }
    },
    "fields": [
      {
        "byte_offset": 0,
        "name": "version",
        "data_type": "U1",
        "description": "Message version (0x00 for this version)",
        "fixed_value": 0
      },
      {
        "byte_offset": 1,
        "name": "nPins",
        "data_type": "U1",
        "description": "The number of I/O pins included"
      },
      {
        "byte_offset": 2,
        "name": "flags",
        "data_type": "X1",
        "description": "Flags",
        "bit_fields": [
          {
            "name": "rtcCalib",
            "bit_offset": 0,
            "num_bits": 1,
            "description": "RTC is calibrated"
          },
          {
            "name": "safeBoot",
            "bit_offset": 1,
            "num_bits": 1,
            "description": "Safeboot mode (0 = inactive, 1 = active)"
          },
          {
            "name": "xtalAbsent",
            "bit_offset": 2,
            "num_bits": 1,
            "description": "RTC xtal has been determined to be absent"
          }
        ]
      },
      {
        "byte_offset": 3,
        "name": "hwVersion",
        "data_type": {
          "array_of": "CH",
          "count": 10
        },
        "description": "Zero-terminated hardware version string (same as that returned in the UBX-MON-VER message)"
      },
      {
        "byte_offset": 13,
        "name": "reserved0",
        "data_type": {
          "array_of": "U1",
          "count": 9
        },
        "reserved": true,
        "description": "Reserved"
      }
    ],
    "repeated_groups": [
      {
        "name": "pinData",
        "count_field": "nPins",
        "byte_length": 6,
        "fields": [
          {
            "byte_offset": 0,
            "name": "reserved1",
            "data_type": "U1",
            "reserved": true,
            "description": "Reserved"
          },
          {
            "byte_offset": 1,
            "name": "pinId",
            "data_type": "U1",
            "description": "Identifier for the pin, including both external and internal pins"
          },
          {
            "byte_offset": 2,
            "name": "pinMask",
            "data_type": "X2",
            "description": "Pin mask",
            "bit_fields": [
              {
                "name": "periphPIO",
                "bit_offset": 0,
                "num_bits": 1,
                "description": "Pin is set to peripheral or PIO? 0=Peripheral 1=PIO"
              },
              {
                "name": "pinBank",
                "bit_offset": 1,
                "num_bits": 3,
                "description": "Bank the pin belongs to, where 0=A 1=B 2=C 3=D 4=E 5=F 6=G 7=H"
              },
              {
                "name": "direction",
                "bit_offset": 4,
                "num_bits": 1,
                "description": "Pin direction? 0=Input 1=Output"
              },
              {
                "name": "value",
                "bit_offset": 5,
                "num_bits": 1,
                "description": "Pin value? 0=Low 1=High"
              },
              {
                "name": "vpManager",
                "bit_offset": 6,
                "num_bits": 1,
                "description": "Used by virtual pin manager? 0=No 1=Yes"
              },
              {
                "name": "pioIrq",
                "bit_offset": 7,
                "num_bits": 1,
                "description": "Interrupt enabled? 0=No 1=Yes"
              },
              {
                "name": "pioPullHigh",
                "bit_offset": 8,
                "num_bits": 1,
                "description": "Using pull high resistor? 0=No 1=Yes"
              },
              {
                "name": "pioPullLow",
                "bit_offset": 9,
                "num_bits": 1,
                "description": "Using pull low resistor 0=No 1=Yes"
              }
            ]
          },
          {
            "byte_offset": 4,
            "name": "VP",
            "data_type": "U1",
            "description": "Virtual pin mapping"
          },
          {
            "byte_offset": 5,
            "name": "reserved2",
            "data_type": "U1",
            "reserved": true,
            "description": "Reserved"
          }
        ]
      }
    ]
  }
}
