{
  "ubx_message": {
    "name": "UBX-MON-VER",
    "class_id": "0x0a",
    "message_id": "0x04",
    "message_type": "polled",
    "description": "Poll receiver and software version / Receiver and software version",
    "request_payload": {
      "payload_length": {
        "fixed": 0
      },
      "fields": []
    },
    "response_payload": {
      "payload_length": {
        "variable": {
          "base": 40,
          "formula": "40 + N * 30"
        }
      },
      "fields": [
        {
          "byte_offset": 0,
          "name": "swVersion",
          "data_type": {
            "array_of": "CH",
            "count": 30
          },
          "description": "Nul-terminated software version string."
        },
        {
          "byte_offset": 30,
          "name": "hwVersion",
          "data_type": {
            "array_of": "CH",
            "count": 10
          },
          "description": "Nul-terminated hardware version string"
        },
        {
          "byte_offset": 40,
          "name": "extension_group",
          "repeated_group": {
            "id": "extension_fields",
            "name": "extension",
            "count_field": "N",
            "description": "Extended software information strings. A series of nul-terminated strings. Each extension field is 30 characters long. 'N' is the number of extension strings, implicitly derived from the total message length: N = (payload_length - 40) / 30.",
            "fields": [
              {
                "byte_offset": 0,
                "name": "extension_string",
                "data_type": {
                  "array_of": "CH",
                  "count": 30
                },
                "description": "Extended software information string."
              }
            ]
          }
        }
      ]
    }
  }
}
