parts:
{
  "ubx_message": {
    "name": "UBX-NAV-SAT",
    "class_id": "0x01",
    "message_id": "0x35",
    "message_type": "periodic_polled",
    "payload_length": {
      "variable": {
        "base": 8,
        "formula": "8 + N*12",
        "num_items_field": "numSvs"
      }
    },
    "fields": [
      {
        "byte_offset": 0,
        "name": "iTOW",
        "data_type": "U4",
        "unit": "ms",
        "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details."
      },
      {
        "byte_offset": 4,
        "name": "version",
        "data_type": "U1",
        "fixed_value": 1,
        "description": "Message version (0x01 for this version)"
      },
      {
        "byte_offset": 5,
        "name": "numSvs",
        "data_type": "U1",
        "description": "Number of satellites"
      },
      {
        "byte_offset": 6,
        "name": "reserved0",
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "reserved": true
      },
      {
        "byte_offset": 8,
        "name": "repeated_group",
        "data_type": {
          "repeated_group": {
            "num_items_field": "numSvs",
            "item_size_bytes": 12,
            "fields": [
              {
                "byte_offset": 0,
                "name": "gnssId",
                "data_type": "U1",
                "description": "GNSS identifier (see Satellite Numbering) for assignment"
              },
              {
                "byte_offset": 1,
                "name": "svId",
                "data_type": "U1",
                "description": "Satellite identifier (see Satellite Numbering) for assignment"
              },
              {
                "byte_offset": 2,
                "name": "cno",
                "data_type": "U1",
                "unit": "dBHz",
                "description": "Carrier to noise ratio (signal strength)"
              },
              {
                "byte_offset": 3,
                "name": "elev",
                "data_type": "I1",
                "unit": "deg",
                "description": "Elevation (range: +/-90), unknown if out of range"
              },
              {
                "byte_offset": 4,
                "name": "azim",
                "data_type": "I2",
                "unit": "deg",
                "description": "Azimuth (range 0-360), unknown if elevation is out of range"
              },
              {
                "byte_offset": 6,
                "name": "prRes",
                "data_type": "I2",
                "scale": {
                  "raw": "0.1",
                  "multiplier": 0.1
                },
                "unit": "m",
                "description": "Pseudorange residual"
              },
              {
                "byte_offset": 8,
                "name": "flags",
                "data_type": "X4",
                "description": "Bitmask",
                "bitmask_fields": [
                  {
                    "name": "qualityInd",
                    "bit_offset": 0,
                    "bit_length": 3,
                    "data_type": "U:3",
                    "description": "Signal quality indicator:",
                    "values": [
                      {
                        "value": 0,
                        "description": "no signal"
                      },
                      {
                        "value": 1,
                        "description": "searching signal"
                      },
                      {
                        "value": 2,
                        "description": "signal acquired"
                      },
                      {
                        "value": 3,
                        "description": "signal detected but unusable"
                      },
                      {
                        "value": 4,
                        "description": "code locked and time synchronized"
                      },
                      {
                        "value": 5,
                        "description": "code and carrier locked and time synchronized"
                      },
                      {
                        "value": 6,
                        "description": "code and carrier locked and time synchronized"
                      },
                      {
                        "value": 7,
                        "description": "code and carrier locked and time synchronized"
                      }
                    ]
                  },
                  {
                    "name": "svUsed",
                    "bit_offset": 3,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = Signal in the subset specified in Signal Identifiers is currently being used for navigation"
                  },
                  {
                    "name": "health",
                    "bit_offset": 4,
                    "bit_length": 2,
                    "data_type": "U:2",
                    "description": "Signal health flag:",
                    "values": [
                      {
                        "value": 0,
                        "description": "unknown"
                      },
                      {
                        "value": 1,
                        "description": "healthy"
                      },
                      {
                        "value": 2,
                        "description": "unhealthy"
                      }
                    ]
                  },
                  {
                    "name": "diffCorr",
                    "bit_offset": 6,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = differential correction data is available for this SV"
                  },
                  {
                    "name": "smoothed",
                    "bit_offset": 7,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = carrier smoothed pseudorange used"
                  },
                  {
                    "name": "orbitSource",
                    "bit_offset": 8,
                    "bit_length": 3,
                    "data_type": "U:3",
                    "description": "Orbit source:",
                    "values": [
                      {
                        "value": 0,
                        "description": "no orbit information is available for this SV"
                      },
                      {
                        "value": 1,
                        "description": "ephemeris is used"
                      },
                      {
                        "value": 2,
                        "description": "almanac is used"
                      },
                      {
                        "value": 3,
                        "description": "AssistNow Offline orbit is used"
                      },
                      {
                        "value": 4,
                        "description": "AssistNow Autonomous orbit is used"
                      },
                      {
                        "value": 5,
                        "description": "other orbit information is used"
                      },
                      {
                        "value": 6,
                        "description": "other orbit information is used"
                      },
                      {
                        "value": 7,
                        "description": "other orbit information is used"
                      }
                    ]
                  },
                  {
                    "name": "ephAvail",
                    "bit_offset": 11,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = ephemeris is available for this SV"
                  },
                  {
                    "name": "almAvail",
                    "bit_offset": 12,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = almanac is available for this SV"
                  },
                  {
                    "name": "anoAvail",
                    "bit_offset": 13,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = AssistNow Offline data is available for this SV"
                  },
                  {
                    "name": "aopAvail",
                    "bit_offset": 14,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = AssistNow Autonomous data is available for this SV"
                  },
                  {
                    "name": "sbasCorrUsed",
                    "bit_offset": 16,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = SBAS corrections have been used for a signal in the subset specified in Signal Identifiers"
                  },
                  {
                    "name": "rtcmCorrUsed",
                    "bit_offset": 17,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = RTCM corrections have been used for a signal in the subset specified in Signal Identifiers"
                  },
                  {
                    "name": "slasCorrUsed",
                    "bit_offset": 18,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = QZSS SLAS corrections have been used for a signal in the subset specified in Signal Identifiers"
                  },
                  {
                    "name": "spartnCorrUsed",
                    "bit_offset": 19,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = SPARTN corrections have been used for a signal in the subset specified in Signal Identifiers"
                  },
                  {
                    "name": "prCorrUsed",
                    "bit_offset": 20,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = Pseudorange corrections have been used for a signal in the subset specified in Signal Identifiers"
                  },
                  {
                    "name": "crCorrUsed",
                    "bit_offset": 21,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = Carrier range corrections have been used for a signal in the subset specified in Signal Identifiers"
                  },
                  {
                    "name": "doCorrUsed",
                    "bit_offset": 22,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = Range rate (Doppler) corrections have been used for a signal in the subset specified in Signal Identifiers"
                  },
                  {
                    "name": "clasCorrUsed",
                    "bit_offset": 23,
                    "bit_length": 1,
                    "data_type": "U:1",
                    "description": "1 = CLAS corrections have been used for a signal in the subset specified in Signal Identifiers"
                  }
                ]
              }
            ]
          }
        }
      }
    ]
  }
}
