parts:
{
  "name": "UBX-TIM-TP",
  "class_id": "0x0d",
  "message_id": "0x01",
  "description": "Time pulse time data",
  "message_type": "periodic_polled",
  "payload_length": {
    "fixed": 16
  },
  "fields": [
    {
      "byte_offset": 0,
      "name": "towMS",
      "data_type": "u4",
      "unit": "ms",
      "description": "Time pulse time of week according to time base"
    },
    {
      "byte_offset": 4,
      "name": "towSubMS",
      "data_type": "u4",
      "scale": {
        "raw": "2^-32",
        "multiplier": 2.3283064365386963e-10
      },
      "unit": "ms",
      "description": "Submillisecond part of towMS"
    },
    {
      "byte_offset": 8,
      "name": "qErr",
      "data_type": "i4",
      "unit": "ps",
      "description": "Quantization error of time pulse"
    },
    {
      "byte_offset": 12,
      "name": "week",
      "data_type": "u2",
      "unit": "weeks",
      "description": "Time pulse week number according to time base"
    },
    {
      "byte_offset": 14,
      "name": "flags",
      "data_type": "x1",
      "description": "Flags",
      "bitfield_members": [
        {
          "name": "timeBase",
          "bit_offset": 0,
          "num_bits": 1,
          "description": "Time base information",
          "values": {
            "0": "Time base is GNSS",
            "1": "Time base is UTC"
          }
        },
        {
          "name": "utc",
          "bit_offset": 1,
          "num_bits": 1,
          "description": "UTC available",
          "values": {
            "0": "UTC not available",
            "1": "UTC available"
          }
        },
        {
          "name": "raim",
          "bit_offset": 2,
          "num_bits": 2,
          "description": "(T)RAIM information",
          "values": {
            "0": "Information not available",
            "1": "Not active",
            "2": "Active"
          }
        },
        {
          "name": "qErrInvalid",
          "bit_offset": 4,
          "num_bits": 1,
          "description": "Quantization error validity",
          "values": {
            "0": "Quantization error valid",
            "1": "Quantization error invalid"
          }
        },
        {
          "name": "TpNotLocked",
          "bit_offset": 5,
          "num_bits": 1,
          "description": "Time Pulse lock status",
          "values": {
            "0": "Next TP is locked to GNSS",
            "1": "Next TP is based on local time and not locked to GNSS - week/tow may be invalid"
          }
        }
      ]
    },
    {
      "byte_offset": 15,
      "name": "refInfo",
      "data_type": "x1",
      "description": "Time reference information",
      "bitfield_members": [
        {
          "name": "timeRefGnss",
          "bit_offset": 0,
          "num_bits": 4,
          "description": "GNSS reference information. Only valid if time base is GNSS (timeBase=0).",
          "values": {
            "0": "GPS",
            "1": "GLONASS",
            "2": "BeiDou",
            "3": "Galileo",
            "4": "NavIC",
            "15": "Unknown"
          }
        },
        {
          "name": "utcStandard",
          "bit_offset": 4,
          "num_bits": 4,
          "description": "UTC standard identifier. Only valid if time base is UTC (timeBase=1).",
          "values": {
            "0": "Information not available",
            "1": "Communications Research Laboratory (CRL), Tokyo, Japan",
            "2": "National Institute of Standards and Technology (NIST)",
            "3": "U.S. Naval Observatory (USNO)",
            "4": "International Bureau of Weights and Measures (BIPM)",
            "5": "European laboratories",
            "6": "Former Soviet Union (SU)",
            "7": "National Time Service Center (NTSC), China",
            "8": "National Physics Laboratory India (NPLI)"
          }
        }
      ]
    }
  ]
}
