parts:
{
  "name": "UBX-TIM-VRFY",
  "class_id": "0x0d",
  "message_id": "0x06",
  "message_type": "periodic_polled",
  "description": "Sourced time verification",
  "comment": "This message contains verification information about previous time received via assistance data or from RTC.",
  "payload_length": {
    "fixed": 20
  },
  "fields": [
    {
      "byte_offset": 0,
      "data_type": "I4",
      "name": "itow",
      "unit": "ms",
      "description": "integer millisecond tow received by source"
    },
    {
      "byte_offset": 4,
      "data_type": "I4",
      "name": "frac",
      "unit": "ns",
      "description": "sub-millisecond part of tow"
    },
    {
      "byte_offset": 8,
      "data_type": "I4",
      "name": "deltaMs",
      "unit": "ms",
      "description": "integer milliseconds of delta time (current time minus sourced time)"
    },
    {
      "byte_offset": 12,
      "data_type": "I4",
      "name": "deltaNs",
      "unit": "ns",
      "description": "Sub-millisecond part of delta time"
    },
    {
      "byte_offset": 16,
      "data_type": "U2",
      "name": "wno",
      "unit": "week",
      "description": "Week number"
    },
    {
      "byte_offset": 18,
      "data_type": "X1",
      "name": "flags",
      "description": "Flags\nAiding time source\n• 0 = no time aiding done\n• 2 = source was RTC\n• 3 = source was assistance data"
    },
    {
      "byte_offset": 19,
      "data_type": "U1",
      "name": "reserved0",
      "reserved": true,
      "description": "Reserved"
    }
  ]
}
