parts:
{
  "name": "UBX-NAV-SIG",
  "class_id": "0x01",
  "message_id": "0x43",
  "description": "This message displays information about signals currently tracked or searched by the receiver.",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 8,
        "formula": "8 + numSigs * 16",
        "min": 8
      }
    },
    "fields": [
      {
        "name": "iTOW",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details.",
        "unit": "ms"
      },
      {
        "name": "version",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "numSigs",
        "byte_offset": 5,
        "data_type": "U1",
        "description": "Number of signals"
      },
      {
        "name": "reserved0",
        "byte_offset": 6,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "signal_data",
        "repetition_type": "count_field",
        "count_field": "numSigs",
        "group_size_bytes": 16,
        "base_offset": 8,
        "fields": [
          {
            "name": "gnssId",
            "byte_offset": 0,
            "data_type": "U1",
            "description": "GNSS identifier (see Satellite Numbering) for assignment"
          },
          {
            "name": "svId",
            "byte_offset": 1,
            "data_type": "U1",
            "description": "Satellite identifier (see Satellite Numbering) for assignment"
          },
          {
            "name": "sigId",
            "byte_offset": 2,
            "data_type": "U1",
            "description": "New style signal identifier (see Signal Identifiers)"
          },
          {
            "name": "freqId",
            "byte_offset": 3,
            "data_type": "U1",
            "description": "Only used for GLONASS: This is the frequency slot + 7 (range from 0 to 13)"
          },
          {
            "name": "prRes",
            "byte_offset": 4,
            "data_type": "I2",
            "description": "Pseudorange residual",
            "unit": "m",
            "scale": {
              "raw": "0.1",
              "multiplier": 0.1
            }
          },
          {
            "name": "cno",
            "byte_offset": 6,
            "data_type": "U1",
            "description": "Carrier-to-noise density ratio (signal strength)",
            "unit": "dBHz"
          },
          {
            "name": "qualityInd",
            "byte_offset": 7,
            "data_type": "U1",
            "description": "Signal quality indicator: 0 = no signal, 1 = searching signal, 2 = signal acquired, 3 = signal detected but unusable, 4 = code locked and time synchronized, 5, 6, 7 = code and carrier locked and time synchronized"
          },
          {
            "name": "corrSource",
            "byte_offset": 8,
            "data_type": "U1",
            "description": "Correction source: 0 = no corrections, 1 = SBAS corrections, 2 = BeiDou corrections, 3 = RTCM2 corrections, 4 = RTCM3 OSR corrections, 5 = RTCM3 SSR corrections, 6 = QZSS SLAS corrections, 7 = SPARTN corrections, 8 = CLAS corrections"
          },
          {
            "name": "ionoModel",
            "byte_offset": 9,
            "data_type": "U1",
            "description": "Ionospheric model used: 0 = no model, 1 = Klobuchar model transmitted by GPS, 2 = SBAS model, 3 = Klobuchar model transmitted by BeiDou, 8 = Iono delay derived from dual frequency observations"
          },
          {
            "name": "sigFlags",
            "byte_offset": 10,
            "data_type": "X2",
            "description": "Signal related flags",
            "bitfield": {
              "bits": [
                {
                  "name": "health",
                  "bit_start": 0,
                  "bit_end": 1,
                  "description": "Signal health flag: 0 = unknown, 1 = healthy, 2 = unhealthy"
                },
                {
                  "name": "prSmoothed",
                  "bit_start": 2,
                  "bit_end": 2,
                  "description": "1 = Pseudorange has been smoothed"
                },
                {
                  "name": "prUsed",
                  "bit_start": 3,
                  "bit_end": 3,
                  "description": "1 = Pseudorange has been used for this signal"
                },
                {
                  "name": "crUsed",
                  "bit_start": 4,
                  "bit_end": 4,
                  "description": "1 = Carrier range has been used for this signal"
                },
                {
                  "name": "doUsed",
                  "bit_start": 5,
                  "bit_end": 5,
                  "description": "1 = Range rate (Doppler) has been used for this signal"
                },
                {
                  "name": "prCorrUsed",
                  "bit_start": 6,
                  "bit_end": 6,
                  "description": "1 = Pseudorange corrections have been used for this signal"
                },
                {
                  "name": "crCorrUsed",
                  "bit_start": 7,
                  "bit_end": 7,
                  "description": "1 = Carrier range corrections have been used for this signal"
                },
                {
                  "name": "doCorrUsed",
                  "bit_start": 8,
                  "bit_end": 8,
                  "description": "1 = Range rate (Doppler) corrections have been used for this signal"
                },
                {
                  "name": "authStatus",
                  "bit_start": 9,
                  "bit_end": 9,
                  "description": "Authentication status of the navigation data used to compute the satellite's position in current navigation epoch. If the authentication fails, the navigation data will not be used so the authentication status in this message can only take two values: 0 = Unknown, 1 = Authenticated. Note that currently the only data authentication function is provided by Galileo Open Service Navigation Message Authentication (OSNMA) protocol for E1 I/NAV message."
                }
              ]
            }
          },
          {
            "name": "reserved1",
            "byte_offset": 12,
            "data_type": {
              "array_of": "U1",
              "count": 4
            },
            "description": "Reserved",
            "reserved": true
          }
        ]
      }
    ]
  }
}
