{
  "name": "UBX-MON-SPAN",
  "class_id": "0x0a",
  "message_id": "0x31",
  "description": "This message is to be used as a basic spectrum analyzer, where it displays one spectrum for each of the receiver's existing RF paths. The spectrum is conveyed with the following parameters: The frequency span in Hz, the frequency bin resolution in Hz, the center frequency in Hz, and 256 bins with amplitude data. Additionally, in order to give further insight on the signal captured by the receiver, the current gain of the internal programmable gain amplifier (PGA) is provided. This message gives information for comparative analysis rather than absolute and precise spectrum overview. Users should not expect highly accurate spectrum amplitude. Note that the PGA gain is not included in the spectrum data but is available as a separate field. Neither the spectrum, nor the PGA gain considers the internal fixed LNA gain or an external third-party LNA. The center frequency at each bin, assuming a zero-based bin count, can be computed as f(i) = center + span * (i - 127) / 256",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 4,
        "formula": "4 + N*272",
        "min": 4,
        "count_field": "numRfBlocks"
      }
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x00 for this version)"
      },
      {
        "name": "numRfBlocks",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Number of RF blocks included"
      },
      {
        "name": "reserved0",
        "byte_offset": 2,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      }
    ],
    "repeated_groups": [
      {
        "name": "rfBlockSpectrum",
        "repetition_type": "count_field",
        "count_field": "numRfBlocks",
        "group_size_bytes": 272,
        "base_offset": 4,
        "fields": [
          {
            "name": "spectrum",
            "byte_offset": 0,
            "data_type": {
              "array_of": "U1",
              "count": 256
            },
            "description": "Spectrum data (number of points = span/res) [Uuu.ff dB]",
            "unit": "dB",
            "scale": {
              "raw": "2^-2",
              "multiplier": 0.25
            }
          },
          {
            "name": "span",
            "byte_offset": 256,
            "data_type": "U4",
            "description": "Spectrum span",
            "unit": "Hz"
          },
          {
            "name": "res",
            "byte_offset": 260,
            "data_type": "U4",
            "description": "Resolution of the spectrum",
            "unit": "Hz"
          },
          {
            "name": "center",
            "byte_offset": 264,
            "data_type": "U4",
            "description": "Center of spectrum span",
            "unit": "Hz"
          },
          {
            "name": "pga",
            "byte_offset": 268,
            "data_type": "U1",
            "description": "Programmable gain amplifier",
            "unit": "dB"
          },
          {
            "name": "reserved1",
            "byte_offset": 269,
            "data_type": {
              "array_of": "U1",
              "count": 3
            },
            "description": "Reserved",
            "reserved": true
          }
        ]
      }
    ]
  }
}

