{
  "name": "UBX-UPD-SOS",
  "class_id": "0x09",
  "message_id": "0x14",
  "description": "Backup and restore functions for battery-backed memory (BBR) in flash memory, including polling status, creating/clearing backups, and receiving acknowledgements/status updates.",
  "message_type": "poll_request_command_output",
  "payload": {
    "length": {
      "variable": {
        "min": 0,
        "max": 8
      }
    },
    "fields": [
      {
        "name": "cmd",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Command or status identifier. (Present in 4-byte and 8-byte variants)",
        "bitfield": {
          "bits": [
            {
              "name": "Create backup",
              "bit_start": 0,
              "bit_end": 7,
              "value": 0,
              "description": "Input command: Save part of the battery-backed memory (BBR) in flash."
            },
            {
              "name": "Clear backup",
              "bit_start": 0,
              "bit_end": 7,
              "value": 1,
              "description": "Input command: Erase the backup file present in flash."
            },
            {
              "name": "Backup creation acknowledge",
              "bit_start": 0,
              "bit_end": 7,
              "value": 2,
              "description": "Output message: Confirmation of creation of a backup file in flash."
            },
            {
              "name": "System restored from backup",
              "bit_start": 0,
              "bit_end": 7,
              "value": 3,
              "description": "Output message: Notification that the BBR has been restored from a backup file in flash."
            }
          ]
        }
      },
      {
        "name": "reserved0",
        "byte_offset": 1,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "response",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Response status (only present in 8-byte output messages, i.e., when cmd is 2 or 3).",
        "bitfield": {
          "bits": [
            {
              "name": "Not acknowledged",
              "bit_start": 0,
              "bit_end": 7,
              "value": 0,
              "description": "Response for cmd=2: Backup not acknowledged."
            },
            {
              "name": "Acknowledged",
              "bit_start": 0,
              "bit_end": 7,
              "value": 1,
              "description": "Response for cmd=2: Backup acknowledged."
            },
            {
              "name": "Unknown",
              "bit_start": 0,
              "bit_end": 7,
              "value": 0,
              "description": "Response for cmd=3: Unknown status."
            },
            {
              "name": "Failed restoring from backup",
              "bit_start": 0,
              "bit_end": 7,
              "value": 1,
              "description": "Response for cmd=3: Failed restoring from backup."
            },
            {
              "name": "Restored from backup",
              "bit_start": 0,
              "bit_end": 7,
              "value": 2,
              "description": "Response for cmd=3: System restored from backup."
            },
            {
              "name": "Not restored (no backup)",
              "bit_start": 0,
              "bit_end": 7,
              "value": 3,
              "description": "Response for cmd=3: System not restored (no backup)."
            }
          ]
        }
      },
      {
        "name": "reserved1",
        "byte_offset": 5,
        "data_type": {
          "array_of": "U1",
          "count": 3
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}

