parts:
{
  "name": "UBX-ESF-STATUS",
  "class_id": "0x10",
  "message_id": "0x10",
  "description": "External sensor fusion status",
  "message_type": "output",
  "payload": {
    "length": {
      "variable": {
        "base": 16,
        "formula": "16 + numSens * 4",
        "min": 16
      }
    },
    "fields": [
      {
        "name": "iTOW",
        "byte_offset": 0,
        "data_type": "U4",
        "description": "GPS time of week of the navigation epoch. See section iTOW timestamps in the integration manual for details.",
        "unit": "ms"
      },
      {
        "name": "version",
        "byte_offset": 4,
        "data_type": "U1",
        "description": "Message version (0x02 for this version)"
      },
      {
        "name": "initStatus1",
        "byte_offset": 5,
        "data_type": "X1",
        "description": "Initialization status bitfield, part 1",
        "bitfield": {
          "bits": [
            {
              "name": "wtInitStatus",
              "bit_start": 0,
              "bit_end": 1,
              "description": "Wheel tick factor initialization status (0: off, 1: initializing, 2: initialized)."
            },
            {
              "name": "mntAlgStatus",
              "bit_start": 2,
              "bit_end": 4,
              "description": "Automatic IMU-mount alignment status (0: off, 1: initializing, 2: initialized, 3: initialized)."
            },
            {
              "name": "insInitStatus",
              "bit_start": 5,
              "bit_end": 6,
              "description": "INS initialization status (0: off, 1: initializing, 2: initialized)."
            }
          ]
        }
      },
      {
        "name": "initStatus2",
        "byte_offset": 6,
        "data_type": "X1",
        "description": "Initialization status bitfield, part 2",
        "bitfield": {
          "bits": [
            {
              "name": "imuInitStatus",
              "bit_start": 0,
              "bit_end": 1,
              "description": "IMU initialization status (0: off, 1: initializing, 2: initialized)."
            }
          ]
        }
      },
      {
        "name": "reserved0",
        "byte_offset": 7,
        "data_type": {
          "array_of": "U1",
          "count": 5
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "fusionMode",
        "byte_offset": 12,
        "data_type": "U1",
        "description": "Fusion mode: 0: Initialization mode: receiver is initializing some unknown values required for doing sensor fusion; 1: Fusion mode: GNSS and sensor data are used for navigation solution computation; 2: Suspended fusion mode: sensor fusion is temporarily disabled due to e.g. invalid sensor data or detected ferry; 3: Disabled fusion mode: sensor fusion is permanently disabled until receiver reset due e.g. to sensor error. See the Fusion filter modes section in the integration manual for more details."
      },
      {
        "name": "reserved1",
        "byte_offset": 13,
        "data_type": {
          "array_of": "U1",
          "count": 2
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "numSens",
        "byte_offset": 15,
        "data_type": "U1",
        "description": "Number of sensors"
      }
    ],
    "repeated_groups": [
      {
        "name": "numSens times",
        "repetition_type": "count_field",
        "count_field": "numSens",
        "group_size_bytes": 4,
        "base_offset": 16,
        "fields": [
          {
            "name": "sensStatus1",
            "byte_offset": 0,
            "data_type": "X1",
            "description": "Sensor status, part 1",
            "bitfield": {
              "bits": [
                {
                  "name": "type",
                  "bit_start": 0,
                  "bit_end": 5,
                  "description": "Sensor data type. See section Sensor data types in the integration manual for details."
                },
                {
                  "name": "used",
                  "bit_start": 6,
                  "bit_end": 6,
                  "description": "If set, sensor data is used for the current sensor fusion solution."
                },
                {
                  "name": "ready",
                  "bit_start": 7,
                  "bit_end": 7,
                  "description": "If set, sensor is set up (configuration is available or not required) but not used for computing the current sensor fusion solution."
                }
              ]
            }
          },
          {
            "name": "sensStatus2",
            "byte_offset": 1,
            "data_type": "X1",
            "description": "Sensor status, part 2",
            "bitfield": {
              "bits": [
                {
                  "name": "calibStatus",
                  "bit_start": 0,
                  "bit_end": 1,
                  "description": "00: Sensor is not calibrated; 01: Sensor is calibrating; 10/11: Sensor is calibrated. Good dead reckoning performance is only possible when all used sensors are calibrated. Depending on the quality of the GNSS signals and the sensor data, the sensors may take a longer time to get calibrated."
                },
                {
                  "name": "timeStatus",
                  "bit_start": 2,
                  "bit_end": 3,
                  "description": "00: No data; 01: Reception of the first byte used to tag the measurement; 10: Event input used to tag the measurement; 11: Time tag provided with the data"
                }
              ]
            }
          },
          {
            "name": "freq",
            "byte_offset": 2,
            "data_type": "U1",
            "description": "Observation frequency",
            "unit": "Hz"
          },
          {
            "name": "faults",
            "byte_offset": 3,
            "data_type": "X1",
            "description": "Sensor faults",
            "bitfield": {
              "bits": [
                {
                  "name": "badMeas",
                  "bit_start": 0,
                  "bit_end": 0,
                  "description": "Bad measurements detected"
                },
                {
                  "name": "badTTag",
                  "bit_start": 1,
                  "bit_end": 1,
                  "description": "Bad measurement time-tags detected"
                },
                {
                  "name": "missingMeas",
                  "bit_start": 2,
                  "bit_end": 2,
                  "description": "Missing or time-misaligned measurements detected"
                },
                {
                  "name": "noisyMeas",
                  "bit_start": 3,
                  "bit_end": 3,
                  "description": "High measurement noise-level detected"
                }
              ]
            }
          }
        ]
      }
    ]
  }
}
