{
  "name": "UBX-CFG-NMEA",
  "class_id": "0x06",
  "message_id": "0x17",
  "description": "Extended NMEA protocol configuration V1",
  "message_type": "get_set",
  "payload": {
    "length": {
      "fixed": 20
    },
    "fields": [
      {
        "name": "filter",
        "byte_offset": 0,
        "data_type": "X1",
        "description": "filter flags",
        "bitfield": {
          "bits": [
            {
              "name": "posFilt",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Enable position output for failed or invalid fixes"
            },
            {
              "name": "mskPosFilt",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Enable position output for invalid fixes"
            },
            {
              "name": "timeFilt",
              "bit_start": 2,
              "bit_end": 2,
              "description": "Enable time output for invalid times"
            },
            {
              "name": "dateFilt",
              "bit_start": 3,
              "bit_end": 3,
              "description": "Enable date output for invalid dates"
            },
            {
              "name": "gpsOnlyFilter",
              "bit_start": 4,
              "bit_end": 4,
              "description": "Restrict output to GPS satellites only"
            },
            {
              "name": "trackFilt",
              "bit_start": 5,
              "bit_end": 5,
              "description": "Enable COG output even if COG is frozen"
            }
          ]
        }
      },
      {
        "name": "nmeaVersion",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "0x4b = NMEA version 4.11 (not available in all products)\n0x41 = NMEA version 4.10 (not available in all products)\n0x40 = NMEA version 4.0 (not available in all products)\n0x23 = NMEA version 2.3\n0x21 = NMEA version 2.1"
      },
      {
        "name": "numSV",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Maximum number of SVs to report per TalkerId.\n0 = unlimited\n8 = 8 SVs\n12 = 12 SVs\n16 = 16 SVs"
      },
      {
        "name": "flags",
        "byte_offset": 3,
        "data_type": "X1",
        "description": "flags",
        "bitfield": {
          "bits": [
            {
              "name": "compat",
              "bit_start": 0,
              "bit_end": 0,
              "description": "enable compatibility mode. This might be needed for certain applications when customer's NMEA parser expects a fixed number of digits in position coordinates."
            },
            {
              "name": "consider",
              "bit_start": 1,
              "bit_end": 1,
              "description": "enable considering mode."
            },
            {
              "name": "limit82",
              "bit_start": 2,
              "bit_end": 2,
              "description": "enable strict limit to 82 characters maximum."
            },
            {
              "name": "highPrec",
              "bit_start": 3,
              "bit_end": 3,
              "description": "enable high precision mode. This flag cannot be set in conjunction with either compatibility mode or Limit82 mode (not supported for protocol versions less than 20.01)."
            }
          ]
        }
      },
      {
        "name": "gnssToFilter",
        "byte_offset": 4,
        "data_type": "X4",
        "description": "Filters out satellites based on their GNSS. If a bitfield is enabled, the corresponding satellites will be not output.",
        "bitfield": {
          "bits": [
            {
              "name": "gps",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Disable reporting of GPS satellites"
            },
            {
              "name": "sbas",
              "bit_start": 1,
              "bit_end": 1,
              "description": "Disable reporting of SBAS satellites"
            },
            {
              "name": "galileo",
              "bit_start": 2,
              "bit_end": 2,
              "description": "Disable reporting of Galileo satellites"
            },
            {
              "name": "qzss",
              "bit_start": 4,
              "bit_end": 4,
              "description": "Disable reporting of QZSS satellites"
            },
            {
              "name": "glonass",
              "bit_start": 5,
              "bit_end": 5,
              "description": "Disable reporting of GLONASS satellites"
            },
            {
              "name": "beidou",
              "bit_start": 6,
              "bit_end": 6,
              "description": "Disable reporting of BeiDou satellites"
            }
          ]
        }
      },
      {
        "name": "svNumbering",
        "byte_offset": 8,
        "data_type": "U1",
        "description": "Configures the display of satellites that do not have an NMEA-defined value.\nNote: this does not apply to satellites with an unknown ID.\n0 = Strict - Satellites are not output\n1 = Extended - Use proprietary numbering (see Satellite Numbering)"
      },
      {
        "name": "mainTalkerId",
        "byte_offset": 9,
        "data_type": "U1",
        "description": "By default the main Talker ID (i.e. the Talker ID used for all messages other than GSV) is determined by the GNSS assignment of the receiver's channels (see UBX-CFG-GNSS).\nThis field enables the main Talker ID to be overridden.\n0 = Main Talker ID is not overridden\n1 = Set main Talker ID to 'GP'\n2 = Set main Talker ID to 'GL'\n3 = Set main Talker ID to 'GN'\n4 = Set main Talker ID to 'GA' (not supported for protocol versions less than 15.00)\n5 = Set main Talker ID to 'GB' (not supported for protocol versions less than 15.00)\n6 = Set main Talker ID to 'GQ' (available in NMEA 4.11 and later)"
      },
      {
        "name": "gsvTalkerId",
        "byte_offset": 10,
        "data_type": "U1",
        "description": "By default the Talker ID for GSV messages is GNSS-specific (as defined by NMEA).\nThis field enables the GSV Talker ID to be overridden.\n0 = Use GNSS-specific Talker ID (as defined by NMEA)\n1 = Use the main Talker ID"
      },
      {
        "name": "version",
        "byte_offset": 11,
        "data_type": "U1",
        "description": "Message version (0x01 for this version)"
      },
      {
        "name": "bdsTalkerId",
        "byte_offset": 12,
        "data_type": {
          "array_of": "CH",
          "count": 2
        },
        "description": "Sets the two characters that should be used for the BeiDou Talker ID. If these are set to zero, then the default BeiDou Talker ID will be used."
      },
      {
        "name": "reserved0",
        "byte_offset": 14,
        "data_type": {
          "array_of": "U1",
          "count": 6
        },
        "description": "Reserved",
        "reserved": true
      }
    ]
  }
}

