{
  "name": "UBX-CFG-PM2",
  "class_id": "0x06",
  "message_id": "0x3b",
  "description": "Extended power management configuration",
  "message_type": "get_set",
  "payload": {
    "length": {
      "fixed": 48
    },
    "fields": [
      {
        "name": "version",
        "byte_offset": 0,
        "data_type": "U1",
        "description": "Message version (0x02 for this version). Note: the message version number is the same as for protocol versions 18 up to 22; select correct message version based on the protocol version supported by your firmware."
      },
      {
        "name": "reserved0",
        "byte_offset": 1,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "maxStartupStateDur",
        "byte_offset": 2,
        "data_type": "U1",
        "description": "Maximum time to spend in Acquisition state. If 0: bound disabled.",
        "unit": "S"
      },
      {
        "name": "reserved1",
        "byte_offset": 3,
        "data_type": "U1",
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "flags",
        "byte_offset": 4,
        "data_type": "X4",
        "description": "PSM configuration flags",
        "bitfield": {
          "bits": [
            {
              "name": "reserved_bit0",
              "bit_start": 0,
              "bit_end": 0,
              "description": "Reserved",
              "reserved": true
            },
            {
              "name": "optTarget",
              "bit_start": 1,
              "bit_end": 3,
              "description": "Optimization target (not supported for protocol versions 32.00 and later)\n000 performance (default)\n001 power save\n010 reserved\n011 reserved\n100 reserved\n101 reserved\n110 reserved\n111 reserved"
            },
            {
              "name": "extintSel",
              "bit_start": 4,
              "bit_end": 4,
              "description": "EXTINT pin select\n0 EXTINT0\n1 EXTINT1"
            },
            {
              "name": "extintWake",
              "bit_start": 5,
              "bit_end": 5,
              "description": "EXTINT pin control\n0 disabled\n1 enabled, keep receiver awake as long as selected EXTINT pin is 'high'"
            },
            {
              "name": "extintBackup",
              "bit_start": 6,
              "bit_end": 6,
              "description": "EXTINT pin control\n0 disabled\n1 enabled, force receiver into BACKUP mode when selected EXTINT pin is 'low'"
            },
            {
              "name": "extintInactive",
              "bit_start": 7,
              "bit_end": 7,
              "description": "EXTINT pin control\n0 disabled\n1 enabled, force backup in case EXTINT pin is inactive for time longer than extintincactivityMs"
            },
            {
              "name": "limitPeakCurr",
              "bit_start": 8,
              "bit_end": 9,
              "description": "Limit peak current\n00 disabled\n01 enabled, peak current is limited\n10 reserved\n11 reserved"
            },
            {
              "name": "waitTimeFix",
              "bit_start": 10,
              "bit_end": 10,
              "description": "Wait for Timefix\n0 wait for normal fix OK before starting on time\n1 wait for time fix OK before starting on time"
            },
            {
              "name": "updateRTC",
              "bit_start": 11,
              "bit_end": 11,
              "description": "Update real time clock\n0 do not wake up to update RTC. RTC is updated during normal on-time.\n1 update RTC. The receiver adds extra wake-up cycles to update the RTC. (not supported for protocol versions 23.00 to 23.01, and 32.00 and later)"
            },
            {
              "name": "updateEPH",
              "bit_start": 12,
              "bit_end": 12,
              "description": "Update ephemeris\n0 do not wake up to update Ephemeris data\n1 update Ephemeris. The receiver adds extra wake-up cycles to update the Ephemeris data."
            },
            {
              "name": "reserved_bit13",
              "bit_start": 13,
              "bit_end": 15,
              "description": "Reserved",
              "reserved": true
            },
            {
              "name": "doNotEnterOff",
              "bit_start": 16,
              "bit_end": 16,
              "description": "Behavior of receiver in case of no fix\n0 receiver enters (Inactive) Awaiting next search state\n1 receiver does not enter (Inactive) Awaiting next search state but keeps trying to acquire a fix instead"
            },
            {
              "name": "mode",
              "bit_start": 17,
              "bit_end": 18,
              "description": "Mode of operation\n00 ON/OFF operation\n01 cyclic tracking operation\n10 reserved\n11 reserved"
            },
            {
              "name": "reserved_bit19",
              "bit_start": 19,
              "bit_end": 31,
              "description": "Reserved",
              "reserved": true
            }
          ]
        }
      },
      {
        "name": "updatePeriod",
        "byte_offset": 8,
        "data_type": "U4",
        "description": "Position update period. If set to 0, the receiver will never retry a fix and it will wait for external events (only affects the update period in ON/OFF operation for protocol versions 32.00 and later).",
        "unit": "ms"
      },
      {
        "name": "searchPeriod",
        "byte_offset": 12,
        "data_type": "U4",
        "description": "Acquisition retry period if previously failed. If set to 0, the receiver will never retry a startup.",
        "unit": "ms"
      },
      {
        "name": "gridOffset",
        "byte_offset": 16,
        "data_type": "U4",
        "description": "Grid offset relative to GPS start of week",
        "unit": "ms"
      },
      {
        "name": "onTime",
        "byte_offset": 20,
        "data_type": "U2",
        "description": "Time to stay in Tracking state",
        "unit": "S"
      },
      {
        "name": "minAcqTime",
        "byte_offset": 22,
        "data_type": "U2",
        "description": "Minimal search time",
        "unit": "S"
      },
      {
        "name": "reserved2",
        "byte_offset": 24,
        "data_type": {
          "array_of": "U1",
          "count": 20
        },
        "description": "Reserved",
        "reserved": true
      },
      {
        "name": "extintInactivityMs",
        "byte_offset": 44,
        "data_type": "U4",
        "description": "inactivity time out on EXTINT pin if enabled",
        "unit": "ms"
      }
    ]
  }
}

